SELECT MB.ITEM_ID , MI.ITEM_NM , MB.WORK_CENTER_CD , MWC.WORK_CENTER_NM , MB.ROUTING_ID , MR.ROUTING_NM , MB.PRIORITY , MB.BOR_ID FROM MOM_BOR MB , MOM_ITEM MI , MOM_WORK_CENTER MWC , MOM_ROUTING MR WHERE MB.COMPANY_CD = MI.COMPANY_CD AND MB.DIVISION_CD = MI.DIVISION_CD AND MB.ITEM_ID = MI.ITEM_ID AND MB.COMPANY_CD = MWC.COMPANY_CD AND MB.DIVISION_CD = MWC.DIVISION_CD AND MB.WORK_CENTER_CD = MWC.WORK_CENTER_CD AND MB.COMPANY_CD = MR.COMPANY_CD AND MB.DIVISION_CD = MR.DIVISION_CD AND MB.ROUTING_ID = MR.ROUTING_ID AND MB.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MB.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MB.ITEM_ID = #{itemId, jdbcType=VARCHAR}
AND MB.USE_YN = 'Y' ORDER BY MB.PRIORITY,MI.ITEM_NM

SELECT MB.ITEM_ID , MI.ITEM_NM , MB.WORK_CENTER_CD , MWC.WORK_CENTER_NM , MB.ROUTING_ID , MR.ROUTING_NM , MB.PRIORITY , MB.BOR_ID FROM MOM_BOR MB , MOM_ITEM MI , MOM_WORK_CENTER MWC , MOM_ROUTING MR WHERE MB.COMPANY_CD = MI.COMPANY_CD AND MB.DIVISION_CD = MI.DIVISION_CD AND MB.ITEM_ID = MI.ITEM_ID AND MB.COMPANY_CD = MWC.COMPANY_CD AND MB.DIVISION_CD = MWC.DIVISION_CD AND MB.WORK_CENTER_CD = MWC.WORK_CENTER_CD AND MB.COMPANY_CD = MR.COMPANY_CD AND MB.DIVISION_CD = MR.DIVISION_CD AND MB.ROUTING_ID = MR.ROUTING_ID AND MB.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MB.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MB.ITEM_ID = #{itemId, jdbcType=VARCHAR}
AND MB.USE_YN = 'Y' ORDER BY MB.PRIORITY,MI.ITEM_NM

SELECT A.UNIT_CATEGORY_NM , A.UNIT_NM , A.CONVERSION_UNIT_NM , A.UNIT_QTY , A.CONVERSION_UNIT_QTY , A.UNIT_CATEGORY , A.UNIT , A.CONVERSION_UNIT , A.KEY_ID FROM (SELECT (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MC.COMPANY_CD,MC.DIVISION_CD, 'SM0016', MUP.UNIT_CATEGORY, 'KR') FROM DUAL) AS UNIT_CATEGORY_NM , MUP.UNIT_NM , MUC.UNIT_NM AS CONVERSION_UNIT_NM , MC.UNIT_QTY , MC.CONVERSION_UNIT_QTY , MUP.UNIT_CATEGORY , MC.UNIT , MC.CONVERSION_UNIT , MUP.UNIT_CATEGORY || '_'|| MC.UNIT || '_' || MC.CONVERSION_UNIT AS KEY_ID FROM MOM_UNIT_CONVERSION MC , MOM_UNIT MUP , MOM_UNIT MUC WHERE MC.COMPANY_CD = MUP.COMPANY_CD AND MC.DIVISION_CD = MUP.DIVISION_CD AND MC.UNIT = MUP.UNIT AND MC.COMPANY_CD = MUC.COMPANY_CD AND MC.DIVISION_CD = MUC.DIVISION_CD AND MC.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MC.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MUP.UNIT_CATEGORY = #{keyId1, jdbcType=VARCHAR}
AND MC.CONVERSION_UNIT = MUC.UNIT ) A ORDER BY A.UNIT_CATEGORY_NM , A.UNIT_NM , A.CONVERSION_UNIT_NM


SELECT MC.CODE AS LANG_CD2
      ,MC.CODE_NM AS LANG_NM
FROM   MOM_CODE MC
WHERE  MC.COMPANY_CD   = #{companyCd, jdbcType=VARCHAR} 
AND    MC.DIVISION_CD  = #{divisionCd, jdbcType=VARCHAR}
AND    MC.GROUP_CD     = #{groupCd, jdbcType=VARCHAR}
AND    MC.USE_YN = 'Y'

SELECT MPD.PUCHASE_REQUEST_ID , MPM.PR_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0001', MPM.PR_TYPE, 'KR') FROM DUAL) AS PR_TYPE_NM , MPM.PR_TITLE , TO_CHAR(MPD.DUE_DATE ,'YYYY-MM-DD') AS REQUEST_DUE_DATE , MPM.REQUEST_DEPT_CD , (SELECT MOM_COMMON_PKG.FN_GET_DEPARTMENT_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.REQUEST_DEPT_CD) FROM DUAL) AS REQUEST_DEPT_NM , MPD.PO_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.PO_USER_NO) FROM DUAL ) AS PO_USER_NM , MPD.ITEM_ID , MPD.CONFIRM_QTY AS REQUEST_QTY , MPD.PO_QTY , MPD.CONFIRM_QTY - NVL(MPD.PO_QTY, 0) AS REMAIN_QTY , MPD.CONFIRM_QTY - NVL(MPD.PO_QTY, 0) AS PO_REQUEST_QTY , MPD.UNIT_PRICE AS REF_UNIT_PRICE , ( SELECT MOM_COMMON_PKG.FN_GET_UNIT_PRICE(MPD.COMPANY_CD, MPD.DIVISION_CD,'P', 'MAIN 화면의VENDOR_CD',MPD.ITEM_ID,MPD.CURRENCY_CD, 'F', MOM_COMMON_PKG.FN_GET_LOCAL_TIME(MPD.COMPANY_CD,MPD.DIVISION_CD)) FROM DUAL) AS CONFIRM_UNIT_PRICE , MI.VENDOR_CD AS REF_VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPD.COMPANY_CD,MPD.DIVISION_CD,MI.VENDOR_CD) FROM DUAL) AS REF_VENDOR_NM , MPD.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'SM0017', MPD.CURRENCY_CD, 'KR') FROM DUAL) AS CURRENCY_NM , MPD.EXCHANGE_RATE , MPD.AMOUNT , MPD.LOCAL_AMOUNT , MI.IN_LOCATION_CD AS DEPARTURE_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MPD.COMPANY_CD,MPD.DIVISION_CD,MI.IN_LOCATION_CD) FROM DUAL) AS DEPARTURE_LOCATION_NM , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, 'KR') FROM DUAL) AS ITEM_TYPE_NM , MI.PURCHASE_UNIT , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.PURCHASE_UNIT) FROM DUAL) AS PURCHASE_UNIT_NM , MI.MAKER , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0007', MI.MAKER, 'KR') FROM DUAL) AS MAKER_NM , MI.DEPARTURE_VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPD.COMPANY_CD,MPD.DIVISION_CD,MI.DEPARTURE_VENDOR_CD) FROM DUAL) AS DEPARTURE_VENDOR_NM , MPM.REFERENCE_NO , MPD.DESCRIPTION , TO_CHAR(MPM.REQUEST_DATE ,'YYYY-MM-DD') AS REQUEST_DATE , MPM.REQUEST_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.REQUEST_USER_NO) FROM DUAL ) AS REQUEST_USER_NM , TO_CHAR(MPM.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE , MPM.CONFIRM_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_DEPARTMENT_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.CONFIRM_USER_NO) FROM DUAL) AS CONFIRM_USER_NM , TO_CHAR(MPD.RECEIVE_DATE ,'YYYY-MM-DD') AS RECEIVE_DATE , MPD.RECEIVE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.RECEIVE_USER_NO) FROM DUAL ) AS RECEIVE_USER_NM , MI.PROCUREMENT_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0004', MI.PROCUREMENT_TYPE, 'KR') FROM DUAL) AS PROCUREMENT_TYPE_NM , MPD.COMPANY_CD , MPD.DIVISION_CD , MPD.PR_NO , MPD.PR_SEQ , NVL(MI.TEST_REPORT_FLAG, 'N') AS TEST_REPORT_FLAG , NVL(MI.IQC_FLAG, 'N') AS IQC_FLAG , NVL(MI.LOT_MANAGEMENT_YN, 'N') AS LOT_MANAGEMENT_YN , NVL(MI.PRICE_SYNC_YN, 'N') AS PRICE_SYNC_YN , NVL(MI.PO_OVER_RECEIPT_RATE, 20) AS PO_OVER_RECEIPT_RATE FROM MOM_PR_DTL MPD , MOM_PR_MST MPM , MOM_ITEM MI WHERE MPD.COMPANY_CD = MPM.COMPANY_CD AND MPD.DIVISION_CD = MPM.DIVISION_CD AND MPD.PR_NO = MPM.PR_NO AND MPD.COMPANY_CD = MI.COMPANY_CD AND MPD.DIVISION_CD = MI.DIVISION_CD AND MPD.ITEM_ID = MI.ITEM_ID AND MPD.STATE = 'PRR' -- 접수건 AND MPD.RECEIVE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MPM.PR_NO) LIKE '%' || UPPER(#{prNo, jdbcType=VARCHAR}) || '%')
AND MPM.REQUEST_DEPT_CD = #{poDeptCd, jdbcType=VARCHAR}
AND (UPPER(MD.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND MI.VENDOR_CD = #{refVendorCd, jdbcType=VARCHAR}
AND MPD.PO_USER_NO = #{poUserNo, jdbcType=VARCHAR}
ORDER BY MPD.PR_NO , MPD.PR_SEQ
