mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MP.PARTNER_CD , MP.PARTNER_NM , MP.PARTNER_NM_ENG , MP.PARTNER_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MP.COMPANY_CD,MP.DIVISION_CD,'SM0018', MP.PARTNER_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL ) AS PARTNER_TYPE_NM , MP.BUSINESS_NO , MP.BUSINESS_ITEM , MP.BUSINESS_TYPE , MP.TEL_NO , MP.FAX_NO , MP.EMAIL , MP.ADDRESS , MP.REPRESENTATIVE_NM , MP.PARTNER_USER_NM , MP.USE_YN , MP.DESCRIPTION , MP.UPDATE_BY , MP.UPDATE_DATE , ( SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MP.COMPANY_CD,MP.DIVISION_CD,MP.UPDATE_BY) FROM DUAL) AS UPDATE_BY_NM FROM MOM_COM_DIV_V MCD , MOM_PARTNER MP WHERE MCD.COMPANY_CD = MP.COMPANY_CD AND MCD.DIVISION_CD = MP.DIVISION_CD AND MCD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (MP.PARTNER_CD LIKE '%' || TRIM(UPPER(#{partnerCd, jdbcType=VARCHAR})) || '%' )
AND (MP.PARTNER_NM LIKE '%' || TRIM(UPPER(#{partnerNm, jdbcType=VARCHAR})) || '%' )
AND MP.PARTNER_TYPE IN (${partnerTypeCd})
AND MP.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MP.PARTNER_NM

mapper.insert:
  
        INSERT INTO MOM_PARTNER(
					              COMPANY_CD
								, DIVISION_CD
								, PARTNER_CD
								, PARTNER_NM
								, PARTNER_NM_ENG
								, PARTNER_TYPE
								, BUSINESS_NO
								, BUSINESS_ITEM
								, BUSINESS_TYPE
								, TEL_NO
								, FAX_NO
								, EMAIL
								, ADDRESS
								, REPRESENTATIVE_NM
								, PARTNER_USER_NM
								, USE_YN
								, DESCRIPTION
								, CREATE_BY
								, CREATE_DATE
								, UPDATE_BY
								, UPDATE_DATE
					         )     
        VALUES
                
				          (    #{item.companyCd,     jdbcType=VARCHAR}
				             , #{item.divisionCd,    jdbcType=VARCHAR}
				             , #{item.partnerCd,     jdbcType=VARCHAR}
				             , #{item.partnerNm,     jdbcType=VARCHAR}
				             , #{item.partnerNmEng,  jdbcType=VARCHAR}
				             , #{item.partnerType,   jdbcType=VARCHAR}
				             , #{item.businessNo,    jdbcType=VARCHAR}
				             , #{item.businessItem,  jdbcType=VARCHAR}
				             , #{item.businessType,  jdbcType=VARCHAR}
				             , #{item.telNo,         jdbcType=VARCHAR}
				             , #{item.faxNo,         jdbcType=VARCHAR}
				             , #{item.email,         jdbcType=VARCHAR}
				             , #{item.address,       jdbcType=INTEGER}
				             , #{item.representativeNm,      jdbcType=VARCHAR}
				             , #{item.partnerUserNm,         jdbcType=VARCHAR}
				             , #{item.useYn,   				 jdbcType=VARCHAR}  
				             , #{item.description,           jdbcType=VARCHAR}    
				             , TRIM(#{item.userId,           jdbcType=VARCHAR})
				             , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
				             , TRIM(#{item.userId,   jdbcType=VARCHAR})
				             , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})  
				          )

update.foreach:
        UPDATE MOM_PARTNER
        SET    PARTNER_NM        = #{item.partnerNm,       jdbcType=VARCHAR}
			 , PARTNER_NM_ENG    = #{item.partnerNmEng,    jdbcType=VARCHAR}
			 , LOCATION_TYPE     = #{item.locationType,    jdbcType=VARCHAR}
			 , PARTNER_CD        = #{item.partnerCd,       jdbcType=VARCHAR}
             , BUSINESS_ITEM     = #{item.businessItem,    jdbcType=VARCHAR}
             , BUSINESS_TYPE     = #{item.businessType,    jdbcType=VARCHAR}
             , SORT_NO           = #{item.telNo,           jdbcType=VARCHAR}
             , SORT_NO           = #{item.faxNo,           jdbcType=VARCHAR}
             , SORT_NO           = #{item.faxNo,           jdbcType=VARCHAR}
             , SORT_NO           = #{item.faxNo,           jdbcType=VARCHAR}
             , SORT_NO           = #{item.faxNo,           jdbcType=VARCHAR}
             , SORT_NO           = #{item.faxNo,           jdbcType=VARCHAR}
		     , USE_YN 		     = #{item.useYn,           jdbcType=VARCHAR}
 		     , DESCRIPTION       = #{item.description,     jdbcType=VARCHAR}
		     , UPDATE_BY         = #{item.userId,          jdbcType=VARCHAR}
		     , UPDATE_DATE       = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR}, #{item.divisionCd, jdbcType=VARCHAR})
         
               COMPANY_CD        = TRIM(#{item.companyCd,  jdbcType=VARCHAR})
         AND   DIVISION_CD       = TRIM(#{item.divisionCd, jdbcType=VARCHAR}) 
         AND   PARTNER_CD        = TRIM(#{item.plantCd,    jdbcType=VARCHAR})
