mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MR.ROUTING_ID , MR.ROUTING_SHORT_NM , MR.ROUTING_NM , MR.USE_YN , MR.DESCRIPTION , MR.CREATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MR.COMPANY_CD, MR.DIVISION_CD, MR.CREATE_BY) FROM DUAL ) AS CREATE_BY_NM , TO_CHAR(MR.CREATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , MR.UPDATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MR.COMPANY_CD, MR.DIVISION_CD, MR.UPDATE_BY) FROM DUAL ) AS UPDATE_BY_NM , TO_CHAR(MR.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE FROM MOM_COM_DIV_V MCD , MOM_ROUTING MR WHERE MCD.COMPANY_CD = MR.COMPANY_CD AND MCD.DIVISION_CD = MR.DIVISION_CD AND MCD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (UPPER(MR.ROUTING_ID) LIKE '%' || UPPER(#{routingId, jdbcType=VARCHAR}) || '%')
AND (UPPER(MR.ROUTING_NM) LIKE '%' || UPPER(#{routingNm, jdbcType=VARCHAR}) || '%')
AND MR.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MCD.COMPANY_NM, MCD.DIVISION_NM, MR.ROUTING_NM

update.foreach:
      	UPDATE MOM_ROUTING						
	    SET    ROUTING_SHORT_NM  = #{item.routingShortNm, jdbcType=VARCHAR}						
		     , ROUTING_NM        = #{item.routingNm, jdbcType=VARCHAR}						
		     , USE_YN            = #{item.useYn, jdbcType=VARCHAR}						
		     , DESCRIPTION       = #{item.description, jdbcType=VARCHAR}						
		     , UPDATE_BY         = #{item.userId, jdbcType=VARCHAR}						
		     , UPDATE_DATE       = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})						
		WHERE  COMPANY_CD        = #{item.companyCd, jdbcType=VARCHAR}						
		AND    DIVISION_CD       = #{item.divisionCd, jdbcType=VARCHAR}						
		AND    ROUTING_ID        = #{item.routingId, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_ROUTING_PATH_UP


 (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
    AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
    AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
 )

insert.foreach:

INTO MOM_PROC_ROUTING_PATH_UP (
             COMPANY_CD
           , DIVISION_CD
           , ROUTING_ID
           , OPERATION_ID
           , FINAL_OPERATION_YN
           , MILESTONE
           , USE_YN
           , DESCRIPTION
           , VAL_MSG
           , MULTI_MSG
           , CREATE_BY
           , CREATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.routingId, jdbcType=VARCHAR}
           , #{item.operationId, jdbcType=VARCHAR}
           , #{item.finalOperationYn, jdbcType=VARCHAR}
           , #{item.milestone, jdbcType=VARCHAR}
           , #{item.useYn, jdbcType=VARCHAR}
           , #{item.description, jdbcType=VARCHAR}
           , #{item.valMsg, jdbcType=VARCHAR}
           , #{item.multiMsg, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

mapper.select:

          { CALL MOM_MASTER_INFO_PKG.sp_update_routing_path(    #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,   javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd,  jdbcType=VARCHAR, mode=IN}												  
					                                          , #{userId,      jdbcType=VARCHAR, mode=IN} )}
