mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MR.ROUTING_ID , MR.ROUTING_NM , MR.USE_YN , MR.DESCRIPTION , MR.CREATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MR.COMPANY_CD, MR.DIVISION_CD, MR.CREATE_BY) FROM DUAL ) AS CREATE_BY_NM , TO_CHAR(MR.CREATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , MR.UPDATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MR.COMPANY_CD, MR.DIVISION_CD, MR.UPDATE_BY) FROM DUAL ) AS UPDATE_BY_NM , TO_CHAR(MR.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE FROM MOM_COM_DIV_V MCD , MOM_ROUTING MR WHERE MCD.COMPANY_CD = MR.COMPANY_CD AND MCD.DIVISION_CD = MR.DIVISION_CD AND MCD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (MR.ROUTING_ID LIKE '%' || TRIM(UPPER(#{routingId, jdbcType=VARCHAR})) || '%' OR MR.ROUTING_NM LIKE '%' || TRIM(UPPER(#{routingId, jdbcType=VARCHAR})))
AND (MR.ROUTING_NM LIKE '%' || TRIM(UPPER(#{routingNm, jdbcType=VARCHAR})))
AND MR.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MCD.COMPANY_NM, MCD.DIVISION_NM, MR.ROUTING_NM

mapper.insert:
INSERT INTO MOM_ROUTING (
            COMPANY_CD
          , DIVISION_CD
          , ROUTING_ID
          , ROUTING_NM
          , USE_YN
          , DESCRIPTION
          , CREATE_BY
          , CREATE_DATE
          , UPDATE_BY
          , UPDATE_DATE )
VALUES 
        (   #{item.companyCd, jdbcType=VARCHAR}
          , #{item.divisionCd, jdbcType=VARCHAR}
          , #{item.routingId, jdbcType=VARCHAR}
          , #{item.routingNm, jdbcType=VARCHAR}
          , #{item.useYn, jdbcType=VARCHAR}
          , #{item.description, jdbcType=VARCHAR}
          , #{item.userId, jdbcType=VARCHAR}
          , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
          , #{item.userId, jdbcType=VARCHAR}
          , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
        )

update.foreach:
UPDATE MOM_ROUTING
SET    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     , DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     , ROUTING_ID = #{item.routingId, jdbcType=VARCHAR}
     , ROUTING_NM = #{item.routingNm, jdbcType=VARCHAR}
     , USE_YN = #{item.useYn, jdbcType=VARCHAR}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    ROUTING_ID = #{item.routingId, jdbcType=VARCHAR}

mapper.insert:
INSERT INTO MOM_ROUTING_PATH (
            COMPANY_CD
          , DIVISION_CD
          , ROUTING_ID
          , OPERATION_ID
          , OPERATION_SEQ
          , FINAL_OPERATION_YN
          , USE_YN
          , DESCRIPTION
          , CREATE_BY
          , CREATE_DATE
          , UPDATE_BY
          , UPDATE_DATE )
VALUES 
        (   #{item.companyCd, jdbcType=VARCHAR}
          , #{item.divisionCd, jdbcType=VARCHAR}
          , #{item.routingId, jdbcType=VARCHAR}
          , #{item.operationId, jdbcType=VARCHAR}
          , #{item.operationSeq, jdbcType=NUMERIC}
          , #{item.finalOperationYn, jdbcType=VARCHAR}
          , #{item.useYn, jdbcType=VARCHAR}
          , #{item.description, jdbcType=VARCHAR}
          , #{item.userId, jdbcType=VARCHAR}
          , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
          , #{item.userId, jdbcType=VARCHAR}
          , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
        )

update.foreach:
UPDATE MOM_ROUTING_PATH
SET    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     , DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     , ROUTING_ID = #{item.routingId, jdbcType=VARCHAR}
     , OPERATION_ID = #{item.operationId, jdbcType=VARCHAR}
     , OPERATION_SEQ = #{item.operationSeq, jdbcType=NUMERIC}
     , FINAL_OPERATION_YN = #{item.finalOperationYn, jdbcType=VARCHAR}
     , USE_YN = #{item.useYn, jdbcType=VARCHAR}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    ROUTING_ID = #{item.routingId, jdbcType=VARCHAR}
