mapper.select:
 SELECT NVL(VAL_MSG,'PASS') AS VAL_MSG
      , MULTI_MSG      
FROM    MOM_PROC_BOR
WHERE   COMPANY_CD  = #{companyCd , jdbcType=VARCHAR}
AND     DIVISION_CD = #{divisionCd , jdbcType=VARCHAR}
AND     CREATE_BY    = #{userId , jdbcType=VARCHAR}

SELECT * FROM ( SELECT COUNT(*) OVER() AS TOTAL_COUNT, A.*, ROWNUM AS KEY_ID, ROWNUM AS ID
FROM ( SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MB.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD,MI.DIVISION_CD,'MD0002',MI.ITEM_TYPE, 'KR') FROM DUAL) AS ITEM_TYPE_NM , MB.ROUTING_ID , (SELECT MOM_COMMON_PKG.FN_GET_ROUTING_NAME(MB.COMPANY_CD,MB.DIVISION_CD,MB.ROUTING_ID) FROM DUAL) AS ROUTING_NM , MB.WORK_CENTER_CD , (SELECT MOM_COMMON_PKG.FN_GET_WORK_CENTER_NAME(MB.COMPANY_CD,MB.DIVISION_CD,MB.WORK_CENTER_CD) FROM DUAL) AS WORK_CENTER_NM , MB.TACT_TIME , MB.TACT_TIME_UOM , MB.PROCESS_LT_UOM , MB.PROCESS_LT , MB.PRIORITY , MB.YIELD , MB.BOR_ID , MB.CONSUME_LOCATION_CD , MB.GOOD_LOCATION_CD , MB.DEFECT_LOCATION_CD ,(SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MB.COMPANY_CD, MB.DIVISION_CD, MB.CONSUME_LOCATION_CD) FROM DUAL ) AS CONSUME_LOCATION_NM ,(SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MB.COMPANY_CD, MB.DIVISION_CD, MB.GOOD_LOCATION_CD) FROM DUAL ) AS GOOD_LOCATION_NM ,(SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MB.COMPANY_CD, MB.DIVISION_CD, MB.DEFECT_LOCATION_CD) FROM DUAL ) AS DEFECT_LOCATION_NM , MB.USE_YN , MB.DESCRIPTION , MB.CREATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MB.COMPANY_CD, MB.DIVISION_CD, MB.CREATE_BY) FROM DUAL ) AS CREATE_BY_NM , TO_CHAR(MB.CREATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , MB.UPDATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MB.COMPANY_CD, MB.DIVISION_CD, MB.UPDATE_BY) FROM DUAL ) AS UPDATE_BY_NM , TO_CHAR(MB.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE FROM MOM_COM_DIV_V MCD , MOM_BOR MB , MOM_ITEM MI WHERE MCD.COMPANY_CD = MB.COMPANY_CD AND MCD.DIVISION_CD = MB.DIVISION_CD AND MB.COMPANY_CD = MI.COMPANY_CD AND MB.DIVISION_CD = MI.DIVISION_CD AND MB.ITEM_ID = MI.ITEM_ID AND MB.COMPANY_CD = #{companyCd , jdbcType=VARCHAR} AND MB.DIVISION_CD = #{divisionCd , jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND MB.WORK_CENTER_CD = #{workCenterCd , jdbcType=VARCHAR}
AND MB.ROUTING_ID = #{routingId , jdbcType=VARCHAR}
AND MB.USE_YN = #{useYn , jdbcType=VARCHAR}
ORDER BY MCD.COMPANY_NM, MCD.DIVISION_NM, MB.ITEM_ID, MB.ROUTING_ID ) A
)
WHERE KEY_ID BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_BOR


  (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
     AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
  )

insert.foreach:

 INTO MOM_PROC_BOR (
            COMPANY_CD
          , DIVISION_CD
          , BOR_ID
          , ITEM_ID
          , ROUTING_ID
          , WORK_CENTER_CD
          , TACT_TIME
          , TACT_TIME_UOM
          , PROCESS_LT
          , PROCESS_LT_UOM
          , PRIORITY
          , YIELD
          , CONSUME_LOCATION_CD
          , GOOD_LOCATION_CD
          , DEFECT_LOCATION_CD
          , USE_YN
          , DESCRIPTION
          , CREATE_BY
          , CREATE_DATE )
VALUES 
        (   #{item.companyCd, jdbcType=VARCHAR}
          , #{item.divisionCd, jdbcType=VARCHAR}
          , #{item.borId, jdbcType=VARCHAR}
          , #{item.itemId, jdbcType=VARCHAR}
          , #{item.routingId, jdbcType=VARCHAR}
          , #{item.workCenterCd, jdbcType=VARCHAR}
          , #{item.tactTime, jdbcType=NUMERIC}
          , #{item.tactTimeUom, jdbcType=VARCHAR}
          , #{item.processLt, jdbcType=NUMERIC}
          , #{item.processLtUom, jdbcType=VARCHAR}
          , #{item.priority, jdbcType=NUMERIC}
          , NVL(#{item.yield, jdbcType=NUMERIC},0)
          , #{item.consumeLocationCd, jdbcType=VARCHAR}
          , #{item.goodLocationCd, jdbcType=VARCHAR}
          , #{item.defectLocationCd, jdbcType=VARCHAR}
          , #{item.useYn, jdbcType=VARCHAR}
          , #{item.description, jdbcType=VARCHAR}
          , #{item.userId, jdbcType=VARCHAR}
          , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
        )

