mapper.select:
SELECT * FROM ( SELECT COUNT(*) OVER() AS TOTAL_COUNT, A.*, ROWNUM AS KEY_ID, ROWNUM AS ID
FROM ( SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MB.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD,MI.DIVISION_CD,'MD0002',MI.ITEM_TYPE, 'KR') FROM DUAL) AS ITEM_TYPE_NM , MB.ROUTING_ID , (SELECT MOM_COMMON_PKG.FN_GET_ROUTING_NAME(MB.COMPANY_CD,MB.DIVISION_CD,MB.ROUTING_ID) FROM DUAL) AS ROUTING_NM , MB.WORK_CENTER_CD , (SELECT MOM_COMMON_PKG.FN_GET_WORK_CENTER_NAME(MB.COMPANY_CD,MB.DIVISION_CD,MB.WORK_CENTER_CD) FROM DUAL) AS WORK_CENTER_NM , MB.TACT_TIME , MB.TACT_TIME_UOM , MB.PROCESS_LT_UOM , MB.PROCESS_LT , MB.PRIORITY , MB.YIELD , MB.BOR_ID , MB.USE_YN , MB.DESCRIPTION , MB.CREATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MB.COMPANY_CD, MB.DIVISION_CD, MB.CREATE_BY) FROM DUAL ) AS CREATE_BY_NM , TO_CHAR(MB.CREATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , MB.UPDATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MB.COMPANY_CD, MB.DIVISION_CD, MB.UPDATE_BY) FROM DUAL ) AS UPDATE_BY_NM , TO_CHAR(MB.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE FROM MOM_COM_DIV_V MCD , MOM_BOR MB , MOM_ITEM MI WHERE MCD.COMPANY_CD = MB.COMPANY_CD AND MCD.DIVISION_CD = MB.DIVISION_CD AND MB.COMPANY_CD = MI.COMPANY_CD AND MB.DIVISION_CD = MI.DIVISION_CD AND MB.ITEM_ID = MI.ITEM_ID AND MB.COMPANY_CD = #{companyCd , jdbcType=VARCHAR} AND MB.DIVISION_CD = #{divisionCd , jdbcType=VARCHAR}
AND (UPPER(MB.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm , jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm , jdbcType=VARCHAR}))
AND MB.WORK_CENTER_CD = #{workCenterCd , jdbcType=VARCHAR}
AND MB.ROUTING_ID = #{routingId , jdbcType=VARCHAR}
AND MB.USE_YN = #{useYn , jdbcType=VARCHAR}
ORDER BY MCD.COMPANY_NM, MCD.DIVISION_NM, MB.ITEM_ID, MB.ROUTING_ID ) A
)
WHERE KEY_ID BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}


          { CALL mom_demand_pkg.sp_create_manual_demand(   #{p_err_code, jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,  javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd, jdbcType=VARCHAR, mode=IN}
					                                          , #{demandType,       jdbcType=VARCHAR, mode=IN} 
					                                          , #{itemId,           jdbcType=VARCHAR, mode=IN}  
					                                          , TO_DATE(#{dueDate,  jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
					                                          , #{qty,              jdbcType=NUMERIC, mode=IN}  
					                                          , #{priority,         jdbcType=NUMERIC, mode=IN}  
					                                          , #{workCenterCd,     jdbcType=VARCHAR, mode=IN}  
					                                          , #{description,      jdbcType=VARCHAR, mode=IN} 
					                                          , #{userId,           jdbcType=VARCHAR, mode=IN} )}

update.foreach:
UPDATE MOM_BOR
SET    ITEM_ID = #{item.itemId, jdbcType=VARCHAR}
     , ROUTING_ID = #{item.routingId, jdbcType=VARCHAR}
     , WORK_CENTER_CD = #{item.workCenterCd, jdbcType=VARCHAR}
     , TACT_TIME = #{item.tactTime, jdbcType=NUMERIC}
     , TACT_TIME_UOM = #{item.tactTimeUom, jdbcType=VARCHAR}
     , PROCESS_LT = #{item.processLt, jdbcType=NUMERIC}
     , PROCESS_LT_UOM = #{item.processLtUom, jdbcType=VARCHAR}
     , PRIORITY = #{item.priority, jdbcType=NUMERIC}
     , YIELD = #{item.yield, jdbcType=NUMERIC}
     , USE_YN = #{item.useYn, jdbcType=VARCHAR}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    BOR_ID = #{item.borId, jdbcType=VARCHAR}
