mapper.select:
SELECT LPAD(LEVEL, DECODE(LEVEL,1,1,(LEVEL*2)-1),'.') AS BOM_LEVEL , LEVEL , MB.PARENT_ITEM_ID , MB.PARENT_ITEM_ID AS PARENT_ITEM_ID2 , MB.CHILD_ITEM_ID , MB.PARENT_ITEM_ID || '_' || MB.CHILD_ITEM_ID AS KEY_ID , MB.REQ_QTY , MB.PHANTOM_FLAG , MB.MRP_FLAG , MB.ISSUE_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD,MI.DIVISION_CD, 'MD0006', MI.ISSUE_TYPE, 'KR') FROM DUAL) AS ISSUE_TYPE_NM , TO_CHAR(MB.START_DATE,'YYYY-MM-DD') AS START_DATE , TO_CHAR(MB.END_DATE,'YYYY-MM-DD') AS END_DATE , MB.SORT_NO , MB.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MB.COMPANY_CD, MB.DIVISION_CD, MB.UPDATE_BY) FROM DUAL ) AS UPDATEBYNM , TO_CHAR(MB.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , MI.ITEM_NM AS CHILD_ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD,MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, 'KR') FROM DUAL) AS ITEM_TYPE_NM , MI.UNIT , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD,MI.DIVISION_CD, 'MD0003', MI.UNIT, 'KR') FROM DUAL) AS UNIT_NM , MI.IN_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MI.COMPANY_CD,MI.DIVISION_CD,MI.IN_LOCATION_CD) FROM DUAL) AS IN_LOCATION_NM , MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MB.DESCRIPTION , CONNECT_BY_ROOT(MB.CHILD_ITEM_ID) AS BOM_ROOT , SYS_CONNECT_BY_PATH(MB.CHILD_ITEM_ID, ' -> ') AS BOM_PATH , CONNECT_BY_ISLEAF AS BOM_LEAF , MB.USE_YN FROM MOM_COM_DIV_V MCD , MOM_BOM MB , MOM_ITEM MI WHERE MCD.COMPANY_CD = MB.COMPANY_CD AND MCD.DIVISION_CD = MB.DIVISION_CD AND MB.COMPANY_CD = MI.COMPANY_CD AND MB.DIVISION_CD = MI.DIVISION_CD AND MB.CHILD_ITEM_ID = MI.ITEM_ID AND MB.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MB.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MB.USE_YN = #{useYn, jdbcType=VARCHAR}
START WITH MB.PARENT_ITEM_ID = #{childItemId, jdbcType=VARCHAR}
AND TO_DATE(#{basicDate, jdbcType=VARCHAR} , 'YYYY-MM-DD') BETWEEN MB.START_DATE AND MB.END_DATE
CONNECT BY PRIOR MB.CHILD_ITEM_ID = MB.PARENT_ITEM_ID
AND TO_DATE(#{basicDate, jdbcType=VARCHAR} , 'YYYY-MM-DD') BETWEEN MB.START_DATE AND MB.END_DATE
START WITH MB.CHILD_ITEM_ID = #{childItemId, jdbcType=VARCHAR}
AND TO_DATE(#{basicDate, jdbcType=VARCHAR} , 'YYYY-MM-DD') BETWEEN MB.START_DATE AND MB.END_DATE
CONNECT BY PRIOR MB.PARENT_ITEM_ID = MB.CHILD_ITEM_ID
AND TO_DATE(#{basicDate, jdbcType=VARCHAR} , 'YYYY-MM-DD') BETWEEN MB.START_DATE AND MB.END_DATE
START WITH MB.PARENT_ITEM_ID = #{childItemId, jdbcType=VARCHAR}
AND TO_DATE(#{basicDate, jdbcType=VARCHAR} , 'YYYY-MM-DD') BETWEEN MB.START_DATE AND MB.END_DATE
CONNECT BY PRIOR MB.CHILD_ITEM_ID = MB.PARENT_ITEM_ID
AND TO_DATE(#{basicDate, jdbcType=VARCHAR} , 'YYYY-MM-DD') BETWEEN MB.START_DATE AND MB.END_DATE

mapper.insert:

       INSERT INTO MOM_BOM ( 
                    COMPANY_CD
				  , DIVISION_CD
				  , PARENT_ITEM_ID
				  , CHILD_ITEM_ID
				  , REQ_QTY
				  , START_DATE
				  , END_DATE
				  , PHANTOM_FLAG
				  , MRP_FLAG
				  , ISSUE_TYPE
				  , SORT_NO
				  , USE_YN
				  , DESCRIPTION
				  , CREATE_BY
				  , CREATE_DATE
				  , UPDATE_BY
				  , UPDATE_DATE )     
        VALUES 
			    ( #{item.companyCd,          jdbcType=VARCHAR}
	            , #{item.divisionCd,         jdbcType=VARCHAR}
	            , #{item.parentItemId,       jdbcType=VARCHAR}
                , #{item.childItemId,        jdbcType=VARCHAR}
                , #{item.reqQty,             jdbcType=NUMERIC}
                , TO_DATE(#{item.startDate,  jdbcType=VARCHAR},'YYYY-MM-DD')
                , TO_DATE(#{item.endDate,    jdbcType=VARCHAR},'YYYY-MM-DD')
                , #{item.phantomFlag,        jdbcType=VARCHAR}
                , #{item.mrpFlag,            jdbcType=VARCHAR}
                , #{item.issueType,          jdbcType=VARCHAR}
                , #{item.sortNo,             jdbcType=NUMERIC}
                , #{item.useYn,              jdbcType=VARCHAR}
                , #{item.description,        jdbcType=VARCHAR}    
                , #{item.userId,        jdbcType=VARCHAR}
                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                , #{item.userId,   jdbcType=VARCHAR}
                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR}) 
	            )

update.foreach:
        UPDATE MOM_BOM
        SET         REQ_QTY              = #{item.reqQty,             jdbcType=VARCHAR}
				  , START_DATE           = #{item.startDate,          jdbcType=VARCHAR}
				  , END_DATE             = #{item.endDate,            jdbcType=VARCHAR}
				  , SORT_NO              = #{item.sortNo,             jdbcType=VARCHAR}
				  , MRP_FLAG             = #{item.mrpFlag,               jdbcType=VARCHAR}
				  , ISSUE_TYPE           = #{item.issueType,          jdbcType=VARCHAR}
	              , PHANTOM_FLAG         = #{item.phantomFlag,        jdbcType=VARCHAR}	         
	              , USE_YN               = #{item.useYn,   		      jdbcType=VARCHAR}  
	              , DESCRIPTION          = #{item.description,        jdbcType=VARCHAR}    
		          , UPDATE_BY            = #{item.userId,             jdbcType=VARCHAR}
		          , UPDATE_DATE          = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR}, #{item.divisionCd, jdbcType=VARCHAR})

               COMPANY_CD        = TRIM(#{item.companyCd,     jdbcType=VARCHAR})
         AND   DIVISION_CD       = TRIM(#{item.divisionCd,    jdbcType=VARCHAR}) 
         AND   PARENT_ITEM_ID    = TRIM(#{item.parentItemId,  jdbcType=VARCHAR})
         AND   CHILD_ITEM_ID     = TRIM(#{item.childItemId,   jdbcType=VARCHAR})
