mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCIM.CUSTOMER_CD ||'_'|| MCIM.CUSTOMER_ITEM_ID || '_' || MCIM.ITEM_ID AS KEY_ID , MCD.DIVISION_CD , MCD.DIVISION_NM , MCIM.CUSTOMER_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MCIM.COMPANY_CD, MCIM.DIVISION_CD, MCIM.CUSTOMER_CD) FROM DUAL ) AS CUSTOMER_NM , MCIM.CUSTOMER_ITEM_ID , MCIM.ITEM_ID , MCIM.REQ_QTY , MI.ITEM_NM , MI.UNIT , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD,MI.DIVISION_CD, 'MD0003', MI.UNIT, 'KR') FROM DUAL) AS UNIT_NM , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD,MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, 'KR') FROM DUAL) AS ITEM_TYPE_NM , MI.ITEM_SPEC , MCIM.USE_YN , MCIM.DESCRIPTION , MCIM.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.UPDATE_BY) FROM DUAL ) AS UPDATEBYNM , MCIM.UPDATE_DATE FROM MOM_COM_DIV_V MCD , MOM_CUSTOMER_ITEM_MAPPING MCIM , MOM_ITEM MI WHERE MCD.COMPANY_CD = MCIM.COMPANY_CD AND MCD.DIVISION_CD = MCIM.DIVISION_CD AND MCIM.COMPANY_CD = MI.COMPANY_CD AND MCIM.DIVISION_CD = MI.DIVISION_CD AND MCIM.ITEM_ID = MI.ITEM_ID AND MCIM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCIM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MCIM.CUSTOMER_CD IN (${customerCd})
AND (MCIM.CUSTOMER_ITEM_ID LIKE '%' || TRIM(UPPER(#{customerItemId, jdbcType=VARCHAR})) || '%' )
AND (MCIM.ITEM_ID LIKE '%' || TRIM(UPPER(#{itemId, jdbcType=VARCHAR})) || '%' )
AND MCIM.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MI.ITEM_NM

mapper.insert:
INSERT INTO MOM_CUSTOMER_ITEM_MAPPING (
            COMPANY_CD
         ,  DIVISION_CD
         ,  CUSTOMER_CD
         ,  CUSTOMER_ITEM_ID
         ,  ITEM_ID
         ,  REQ_QTY
         ,  USE_YN
         ,  DESCRIPTION
         ,  CREATE_BY
         ,  CREATE_DATE
         ,  UPDATE_BY
         ,  UPDATE_DATE )
VALUES 
         (   #{item.compan    yCd, jdbcType=VARCHAR}
         ,   #{item.divisionCd, jdbcType=VARCHAR}
         ,   #{item.customerCd, jdbcType=VARCHAR}
         ,   #{item.customerItemId, jdbcType=VARCHAR}
         ,   #{item.itemId, jdbcType=VARCHAR}
         ,   #{item.reqQty, jdbcType=NUMERIC}
         ,   #{item.useYn, jdbcType=VARCHAR}
         ,   #{item.description, jdbcType=VARCHAR}
         ,   #{item.userId, jdbcType=VARCHAR}
         ,   MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         ,   #{item.userId, jdbcType=VARCHAR}
         ,   MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

update.foreach:
UPDATE MOM_CUSTOMER_ITEM_MAPPING
SET    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
    ,  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
    ,  CUSTOMER_CD = #{item.customerCd, jdbcType=VARCHAR}
    ,  CUSTOMER_ITEM_ID = #{item.customerItemId, jdbcType=VARCHAR}
    ,  ITEM_ID = #{item.itemId, jdbcType=VARCHAR}
    ,  REQ_QTY = #{item.reqQty, jdbcType=NUMERIC}
    ,  USE_YN = #{item.useYn, jdbcType=VARCHAR}
    ,  DESCRIPTION = #{item.description, jdbcType=VARCHAR}
    ,  UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
    ,  UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE 1=1
AND   COMPANY_CD        = TRIM(#{item.companyCd,      jdbcType=VARCHAR})
AND   DIVISION_CD       = TRIM(#{item.divisionCd,     jdbcType=VARCHAR}) 
AND   CUSTOMER_CD       = TRIM(#{item.customerCd,     jdbcType=VARCHAR})      
AND   CUSTOMER_ITEM_ID  = TRIM(#{item.customerItemId, jdbcType=VARCHAR})   
AND   ITEM_ID           = TRIM(#{item.itemId,         jdbcType=VARCHAR})
