mapper.select:
SELECT A.COMPANY_CD , A.COMPANY_NM , A.DIVISION_CD , A.DIVISION_NM , A.ITEM_ID , A.ITEM_NM , A.UNIT_CATEGORY , A.UNIT_CATEGORY_NM , A.UNIT , A.UNIT_NM , A.CONVERSION_UNIT , A.CONVERSION_UNIT_NM , A.UNIT_QTY , A.CONVERSION_UNIT_QTY , A.USE_YN , A.DESCRIPTION , A.UPDATE_BY , A.UPDATEBYNM , A.UPDATE_DATE , A.ITEM_ID|| '_'|| A.UNIT_CATEGORY || '_'|| A.UNIT || '_' || A.CONVERSION_UNIT AS KEY_ID FROM ( SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MIUR.ITEM_ID , MI.ITEM_NM , MUP.UNIT_CATEGORY , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MIUR.COMPANY_CD,MIUR.DIVISION_CD, 'SM0016', MUP.UNIT_CATEGORY, 'KR') FROM DUAL) AS UNIT_CATEGORY_NM , MIUR.UNIT , MUP.UNIT_NM , MIUR.CONVERSION_UNIT , MUC.UNIT_NM AS CONVERSION_UNIT_NM , MIUR.UNIT_QTY , MIUR.CONVERSION_UNIT_QTY , MIUR.USE_YN , MIUR.DESCRIPTION , MIUR.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MIUR.COMPANY_CD, MIUR.DIVISION_CD, MIUR.UPDATE_BY) FROM DUAL ) AS UPDATEBYNM , MIUR.UPDATE_DATE FROM MOM_COM_DIV_V MCD , MOM_ITEM_UNIT_REL MIUR , MOM_ITEM MI , MOM_UNIT MUP , MOM_UNIT MUC WHERE MCD.COMPANY_CD = MIUR.COMPANY_CD AND MCD.DIVISION_CD = MIUR.DIVISION_CD AND MIUR.COMPANY_CD = MI.COMPANY_CD AND MIUR.DIVISION_CD = MI.DIVISION_CD AND MIUR.ITEM_ID = MI.ITEM_ID AND MIUR.COMPANY_CD = MUP.COMPANY_CD AND MIUR.DIVISION_CD = MUP.DIVISION_CD AND MIUR.UNIT = MUP.UNIT AND MIUR.COMPANY_CD = MUC.COMPANY_CD AND MIUR.DIVISION_CD = MUC.DIVISION_CD AND MIUR.CONVERSION_UNIT = MUC.UNIT AND MCD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (MI.ITEM_ID LIKE '%' || TRIM(UPPER(#{itemId, jdbcType=VARCHAR})) || '%' )
AND (MI.ITEM_NM LIKE '%' || TRIM(UPPER(#{itemNm, jdbcType=VARCHAR})) || '%' )
AND MUP.UNIT_CATEGORY = #{unitCategory, jdbcType=VARCHAR}
AND MIUR.UNIT IN (${unit})
AND MIUR.CONVERSION_UNIT IN (${conversionUnit})
) A ORDER BY A.COMPANY_NM , A.DIVISION_NM , A.ITEM_ID , A.UNIT_CATEGORY_NM , A.UNIT_NM , A.CONVERSION_UNIT_NM

SELECT A.UNIT_CATEGORY_NM -- 단위유형명 , A.UNIT_NM -- 기준단위명 , A.CONVERSION_UNIT_NM -- 변환단위명 , A.UNIT_QTY -- 기준계수 , A.CONVERSION_UNIT_QTY -- 변환계수 , A.UNIT_CATEGORY -- 단위유형 , A.UNIT -- 기준단위 , A.CONVERSION_UNIT -- 변환단위 , A.KEY_ID FROM (SELECT (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MC.COMPANY_CD,MC.DIVISION_CD, 'SM0016', MUP.UNIT_CATEGORY, 'KR') FROM DUAL) AS UNIT_CATEGORY_NM , MUP.UNIT_NM , MUC.UNIT_NM AS CONVERSION_UNIT_NM , MC.UNIT_QTY , MC.CONVERSION_UNIT_QTY , MUP.UNIT_CATEGORY , MC.UNIT , MC.CONVERSION_UNIT , MUP.UNIT_CATEGORY || '_'|| MC.UNIT || '_' || MC.CONVERSION_UNIT AS KEY_ID FROM MOM_UNIT_CONVERSION MC , MOM_UNIT MUP , MOM_UNIT MUC WHERE MC.COMPANY_CD = MUP.COMPANY_CD AND MC.DIVISION_CD = MUP.DIVISION_CD AND MC.UNIT = MUP.UNIT AND MC.COMPANY_CD = MUC.COMPANY_CD AND MC.DIVISION_CD = MUC.DIVISION_CD AND MC.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MC.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MUP.UNIT_CATEGORY = #{keyId1, jdbcType=VARCHAR}
AND MC.CONVERSION_UNIT = MUC.UNIT ) A ORDER BY A.UNIT_CATEGORY_NM , A.UNIT_NM , A.CONVERSION_UNIT_NM

insert.foreach:

INTO MOM_ITEM_UNIT_REL (
             COMPANY_CD
           , DIVISION_CD
           , ITEM_ID
           , UNIT
           , CONVERSION_UNIT
           , UNIT_QTY
           , CONVERSION_UNIT_QTY
           , USE_YN
           , DESCRIPTION
           , CREATE_BY
           , CREATE_DATE
           , UPDATE_BY
           , UPDATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.itemId, jdbcType=VARCHAR}
           , #{item.unit, jdbcType=VARCHAR}
           , #{item.conversionUnit, jdbcType=VARCHAR}
           , #{item.unitQty, jdbcType=NUMERIC}
           , #{item.conversionUnitQty, jdbcType=NUMERIC}
           , #{item.useYn, jdbcType=VARCHAR}
           , #{item.description, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

update.foreach:
UPDATE MOM_ITEM_UNIT_REL
SET    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     , DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     , ITEM_ID = #{item.itemId, jdbcType=VARCHAR}
     , UNIT = #{item.unit, jdbcType=VARCHAR}
     , CONVERSION_UNIT = #{item.conversionUnit, jdbcType=VARCHAR}
     , UNIT_QTY = #{item.unitQty, jdbcType=NUMERIC}
     , CONVERSION_UNIT_QTY = #{item.conversionUnitQty, jdbcType=NUMERIC}
     , USE_YN = #{item.useYn, jdbcType=VARCHAR}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    ITEM_ID = #{item.itemId, jdbcType=VARCHAR}
AND    UNIT = #{item.unit, jdbcType=VARCHAR}
AND    CONVERSION_UNIT = #{item.conversionUnit, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_ITEM_UNIT_REL


  (       COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     AND  ITEM_ID = #{item.itemId, jdbcType=VARCHAR}
     AND  UNIT = #{item.unit, jdbcType=VARCHAR}
     AND  CONVERSION_UNIT = #{item.conversionUnit, jdbcType=VARCHAR}
  )
