mapper.select:
SELECT A.* FROM (
SELECT MUP.COMPANY_CD , MUP.DIVISION_CD , MUP.PRICE_CATEGORY , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MUP.COMPANY_CD,MUP.DIVISION_CD, 'MD0011', MUP.PRICE_CATEGORY, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PRICE_CATEGORY_NM , MUP.PARTNER_CD AS VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MUP.COMPANY_CD, MUP.DIVISION_CD, MUP.PARTNER_CD) FROM DUAL ) AS VENDOR_NM , MUP.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD,MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, 'KR') FROM DUAL) AS ITEM_TYPE_NM , MI.UNIT , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD,MI.DIVISION_CD, MI.UNIT) FROM DUAL) AS UNIT_NM , MI.PURCHASE_UNIT , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD,MI.DIVISION_CD, MI.PURCHASE_UNIT) FROM DUAL) AS PURCHASE_UNIT_NM , MUP.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MUP.COMPANY_CD,MUP.DIVISION_CD, 'SM0017', MUP.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MUP.PREV_UNIT_PRICE , MUP.UNIT_PRICE , TO_CHAR(MUP.START_DATE ,'YYYY-MM-DD' ) AS START_DATE , TO_CHAR(MUP.END_DATE ,'YYYY-MM-DD' ) AS END_DATE , MUP.PRICE_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MUP.COMPANY_CD,MUP.DIVISION_CD, 'MD0012', MUP.PRICE_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PRICE_TYPE_NM , MUP.MAIN_PARTNER_YN , MUP.USE_YN , MUP.DESCRIPTION , MUP.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MUP.COMPANY_CD, MUP.DIVISION_CD, MUP.UPDATE_BY) FROM DUAL ) AS UPDATEBYNM , TO_CHAR(MUP.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MUP.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MUP.COMPANY_CD,MUP.DIVISION_CD ORDER BY MUP.PARTNER_CD, MUP.ITEM_ID, MUP.CURRENCY_CD, TO_CHAR(MUP.START_DATE ,'YYYY-MM-DD') ) AS ROW_COUNT FROM MOM_UNIT_PRICE MUP , MOM_ITEM MI WHERE MUP.COMPANY_CD = MI.COMPANY_CD AND MUP.DIVISION_CD = MI.DIVISION_CD AND MUP.ITEM_ID = MI.ITEM_ID AND MUP.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MUP.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MUP.PRICE_CATEGORY = 'P'
AND TO_DATE(#{basicDate, jdbcType=VARCHAR},'YYYY-MM-DD') BETWEEN MUP.START_DATE AND MUP.END_DATE
AND MUP.PARTNER_CD = #{vendorCd, jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || TRIM(UPPER(#{itemId, jdbcType=VARCHAR})) || '%' )
AND (UPPER(MI.ITEM_NM) LIKE '%' || TRIM(UPPER(#{itemNm, jdbcType=VARCHAR})) || '%' )
AND MUP.USE_YN = #{useYn, jdbcType=VARCHAR}
) A WHERE ROW_COUNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
        DELETE FROM MOM_PROC_UNIT_PRICE


	        (       COMPANY_CD     = #{item.companyCd, jdbcType=VARCHAR}
	           AND  DIVISION_CD    = #{item.divisionCd, jdbcType=VARCHAR}
	           AND  PRICE_CATEGORY = #{item.priceCategory, jdbcType=VARCHAR}
	           AND  CREATE_BY      = #{item.userId, jdbcType=VARCHAR}
	        )

insert.foreach:

        INTO MOM_PROC_UNIT_PRICE ( COMPANY_CD
								 , DIVISION_CD
								 , PRICE_CATEGORY
								 , PARTNER_CD
								 , ITEM_ID
								 , CURRENCY_CD
								 , UNIT_PRICE
								 , START_DATE
								 , END_DATE
								 , PRICE_TYPE
								 , MAIN_PARTNER_YN
								 , CREATE_BY
								 , CREATE_DATE
                                 )
        VALUES                   ( #{item.companyCd, jdbcType=VARCHAR}
				                 , #{item.divisionCd, jdbcType=VARCHAR}
				                 , #{item.priceCategory, jdbcType=VARCHAR}
				                 , #{item.vendorCd, jdbcType=VARCHAR}
				                 , #{item.itemId, jdbcType=VARCHAR}
				                 , #{item.currencyCd, jdbcType=VARCHAR}
				                 , #{item.unitPrice, jdbcType=NUMERIC}
				                 , TO_DATE(#{item.startDate, jdbcType=VARCHAR},'YYYY-MM-DD')
				                 , TO_DATE(#{item.endDate, jdbcType=VARCHAR},'YYYY-MM-DD')
				                 , #{item.priceType, jdbcType=VARCHAR}
				                 , #{item.mainPartnerYn, jdbcType=VARCHAR}
				                 , #{item.userId, jdbcType=VARCHAR}
				                 , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
				                 )

mapper.select:

{ CALL MOM_MASTER_INFO_PKG.SP_UPSERT_PRICE( #{p_err_code, jdbcType=VARCHAR, mode=OUT}
                                          , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
                                          , #{companyCd,  javaType=string,  jdbcType=VARCHAR , mode=IN}
                                          , #{divisionCd, jdbcType=VARCHAR, mode=IN}
                                          , #{priceCategory, jdbcType=VARCHAR, mode=IN}
                                          , #{actionMode, jdbcType=VARCHAR, mode=IN}                          
                                          , #{userId,     jdbcType=VARCHAR, mode=IN} )}

update.foreach:
UPDATE MOM_UNIT_PRICE
SET    PREV_UNIT_PRICE = UNIT_PRICE 
     , UNIT_PRICE = #{item.unitPrice, jdbcType=NUMERIC}
     , PRICE_TYPE = #{item.priceType, jdbcType=VARCHAR}
     , MAIN_PARTNER_YN = #{item.mainPartnerYn, jdbcType=VARCHAR}
     , USE_YN = #{item.useYn, jdbcType=VARCHAR}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    PRICE_CATEGORY = #{item.priceCategory, jdbcType=VARCHAR}
AND    PARTNER_CD = #{item.vendorCd, jdbcType=VARCHAR}
AND    ITEM_ID = #{item.itemId, jdbcType=VARCHAR}
AND    CURRENCY_CD = #{item.currencyCd, jdbcType=VARCHAR}
AND    START_DATE = #{item.startDate, jdbcType=VARCHAR}
AND    END_DATE = #{item.endDate, jdbcType=VARCHAR}

mapper.delete:
        DELETE FROM MOM_PROC_UNIT_PRICE


	        (       COMPANY_CD     = #{item.companyCd, jdbcType=VARCHAR}
	           AND  DIVISION_CD    = #{item.divisionCd, jdbcType=VARCHAR}
	           AND  PRICE_CATEGORY = #{item.priceCategory, jdbcType=VARCHAR}
	           AND  CREATE_BY      = #{item.userId, jdbcType=VARCHAR}
	        )

insert.foreach:

        INTO MOM_PROC_UNIT_PRICE ( COMPANY_CD
								 , DIVISION_CD
								 , PRICE_CATEGORY
								 , PARTNER_CD
								 , ITEM_ID
								 , CURRENCY_CD
								 , UNIT_PRICE
								 , START_DATE
								 , END_DATE
								 , PRICE_TYPE
								 , MAIN_PARTNER_YN
								 , CREATE_BY
								 , CREATE_DATE
                                 )
        VALUES                   ( #{item.companyCd, jdbcType=VARCHAR}
				                 , #{item.divisionCd, jdbcType=VARCHAR}
				                 , #{item.priceCategory, jdbcType=VARCHAR}
				                 , #{item.vendorCd, jdbcType=VARCHAR}
				                 , #{item.itemId, jdbcType=VARCHAR}
				                 , #{item.currencyCd, jdbcType=VARCHAR}
				                 , #{item.unitPrice, jdbcType=NUMERIC}
				                 , TO_DATE(#{item.startDate, jdbcType=VARCHAR},'YYYY-MM-DD')
				                 , TO_DATE(#{item.endDate, jdbcType=VARCHAR},'YYYY-MM-DD')
				                 , #{item.priceType, jdbcType=VARCHAR}
				                 , #{item.mainPartnerYn, jdbcType=VARCHAR}
				                 , #{item.userId, jdbcType=VARCHAR}
				                 , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
				                 )

mapper.select:

{ CALL MOM_MASTER_INFO_PKG.SP_UPSERT_PRICE( #{p_err_code, jdbcType=VARCHAR, mode=OUT}
                                          , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
                                          , #{companyCd,  javaType=string,  jdbcType=VARCHAR , mode=IN}
                                          , #{divisionCd, jdbcType=VARCHAR, mode=IN}
                                          , #{priceCategory, jdbcType=VARCHAR, mode=IN}
                                          , #{actionMode, jdbcType=VARCHAR, mode=IN}                          
                                          , #{userId,     jdbcType=VARCHAR, mode=IN} )}
