mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MSRT.REST_TIME_CD , MSRT.REST_TIME_NM , MSRT.START_TIME , MSRT.END_TIME , MSRT.REST_TIME , MSRT.USE_YN , MSRT.DESCRIPTION , MSRT.UPDATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MSRT.COMPANY_CD, MSRT.DIVISION_CD, MSRT.UPDATE_BY) FROM DUAL ) AS UPDATEBYNM , TO_CHAR(MSRT.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , MSRT.REST_TIME_CD AS KEY_ID FROM MOM_COM_DIV_V MCD , MOM_SHIFT_REST_TIME MSRT WHERE MCD.COMPANY_CD = MSRT.COMPANY_CD AND MCD.DIVISION_CD = MSRT.DIVISION_CD AND MCD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MSRT.REST_TIME_CD IN (${restTimeCd})
AND MSRT.REST_TIME_CD = #{restTimeCd2, jdbcType=VARCHAR}
AND MSRT.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MSRT.REST_TIME_NM

insert.foreach:

INTO MOM_SHIFT_REST_TIME (
             COMPANY_CD
           , DIVISION_CD
           , REST_TIME_CD
           , REST_TIME_NM
           , START_TIME
           , END_TIME
           , REST_TIME
           , USE_YN
           , DESCRIPTION
           , CREATE_BY
           , CREATE_DATE
           , UPDATE_BY
           , UPDATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.restTimeCd, jdbcType=VARCHAR}
           , #{item.restTimeNm, jdbcType=VARCHAR}
           , #{item.startTime, jdbcType=VARCHAR}
           , #{item.endTime, jdbcType=VARCHAR}
           , #{item.restTime, jdbcType=NUMERIC}
           , #{item.useYn, jdbcType=VARCHAR}
           , #{item.description, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

update.foreach:
UPDATE MOM_SHIFT_REST_TIME
SET    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     , DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     , REST_TIME_CD = #{item.restTimeCd, jdbcType=VARCHAR}
     , REST_TIME_NM = #{item.restTimeNm, jdbcType=VARCHAR}
     , START_TIME = #{item.startTime, jdbcType=VARCHAR}
     , END_TIME = #{item.endTime, jdbcType=VARCHAR}
     , REST_TIME = #{item.restTime, jdbcType=NUMERIC}
     , USE_YN = #{item.useYn, jdbcType=VARCHAR}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    REST_TIME_CD = #{item.restTimeCd, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_SHIFT_REST_TIME


  (       COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     AND  REST_TIME_CD = #{item.restTimeCd, jdbcType=VARCHAR}
  )
