mapper.select:
SELECT A.* FROM (
SELECT MLP.COMPANY_CD , MLP.DIVISION_CD , MLP.LOCATION_CD , ML.LOCATION_NM , MLP.PROCESS_TYPE_ID , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MLP.COMPANY_CD,MLP.DIVISION_CD, 'MD0028', MLP.PROCESS_TYPE_ID, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PROCESS_TYPE_NM , MLP.USE_YN , MLP.DESCRIPTION , MLP.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MLP.COMPANY_CD, MLP.DIVISION_CD, MLP.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MLP.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MLP.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MLP.COMPANY_CD,MLP.DIVISION_CD ORDER BY MLP.LOCATION_CD, MLP.PROCESS_TYPE_ID) AS ROW_CNT FROM MOM_LOCATION_PROCESS_TYPE MLP , MOM_LOCATION ML WHERE MLP.COMPANY_CD = ML.COMPANY_CD AND MLP.DIVISION_CD = ML.DIVISION_CD AND MLP.LOCATION_CD = ML.LOCATION_CD AND MLP.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MLP.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (UPPER(MLP.LOCATION_CD) LIKE '%' || UPPER(#{locationCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(ML.LOCATION_NM) LIKE '%' || UPPER(#{locationCdNm, jdbcType=VARCHAR}) || '%')
AND MLP.PROCESS_TYPE_ID = #{processTypeId, jdbcType=VARCHAR}
AND MLP.USE_YN = #{useYn, jdbcType=VARCHAR}
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_LOCATION_PROCESS


      (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
         AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
         AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
      )

insert.foreach:

INTO MOM_PROC_LOCATION_PROCESS( COMPANY_CD
                              , DIVISION_CD
                              , LOCATION_CD 
                              , PROCESS_TYPE_ID 
                              , USE_YN
                              , DESCRIPTION
                              , DEL_YN
                              , VAL_MSG
                              , MULTI_MSG
                              , CREATE_BY
                              , CREATE_DATE
                              )
VALUES                        ( #{item.companyCd, jdbcType=VARCHAR}
                              , #{item.divisionCd, jdbcType=VARCHAR}
                              , #{item.locationCd, jdbcType=VARCHAR}
                              , #{item.processTypeId , jdbcType=VARCHAR}
                              , #{item.useYn, jdbcType=VARCHAR}
                              , #{item.description, jdbcType=VARCHAR}
                              , #{item.delYn, jdbcType=VARCHAR}
                              , #{item.valMsg, jdbcType=VARCHAR}
                              , #{item.multiMsg, jdbcType=VARCHAR}
                              , #{item.userId, jdbcType=VARCHAR}
                              , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                              )

mapper.select:

{ CALL MOM_MASTER_INFO_PKG.SP_UPSERT_LOCATION_PROCESS( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                     , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                     , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                     , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                                     , #{actionMode,  jdbcType=VARCHAR, mode=IN}        
                                                     , #{userId,      jdbcType=VARCHAR, mode=IN} )}

SELECT NVL(VAL_MSG,'PASS') AS VAL_MSG
     , MULTI_MSG      
FROM   MOM_PROC_LOCATION_PROCESS
WHERE  COMPANY_CD  = #{companyCd , jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd , jdbcType=VARCHAR}
AND    CREATE_BY    = #{userId , jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_LOCATION_PROCESS


      (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
         AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
         AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
      )

insert.foreach:

INTO MOM_PROC_LOCATION_PROCESS( COMPANY_CD
                              , DIVISION_CD
                              , LOCATION_CD 
                              , PROCESS_TYPE_ID 
                              , USE_YN
                              , DESCRIPTION
                              , DEL_YN
                              , VAL_MSG
                              , MULTI_MSG
                              , CREATE_BY
                              , CREATE_DATE
                              )
VALUES                        ( #{item.companyCd, jdbcType=VARCHAR}
                              , #{item.divisionCd, jdbcType=VARCHAR}
                              , #{item.locationCd, jdbcType=VARCHAR}
                              , #{item.processTypeId , jdbcType=VARCHAR}
                              , #{item.useYn, jdbcType=VARCHAR}
                              , #{item.description, jdbcType=VARCHAR}
                              , 'N'
                              , #{item.valMsg, jdbcType=VARCHAR}
                              , #{item.multiMsg, jdbcType=VARCHAR}
                              , #{item.userId, jdbcType=VARCHAR}
                              , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                              )

mapper.select:

{ CALL MOM_MASTER_INFO_PKG.SP_UPSERT_LOCATION_PROCESS( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                     , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                     , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                     , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                                     , #{actionMode,  jdbcType=VARCHAR, mode=IN}        
                                                     , #{userId,      jdbcType=VARCHAR, mode=IN} )}
