mapper.select:
SELECT A.* FROM (
SELECT MLR.COMPANY_CD , MLR.DIVISION_CD , MLR.RESOURCE_GROUP_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MLR.COMPANY_CD,MLR.DIVISION_CD, 'MD0036', MLR.RESOURCE_GROUP_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS RESOURCE_GROUP_NM , MLR.RESOURCE_CD , MLR.RESOURCE_NM , MLR.USE_YN , MLR.DESCRIPTION , MLR.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MLR.COMPANY_CD, MLR.DIVISION_CD, MLR.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MLR.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MLR.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MLR.COMPANY_CD,MLR.DIVISION_CD ORDER BY MLR.RESOURCE_GROUP_CD, MLR.RESOURCE_CD) AS ROW_CNT FROM MOM_LOGISTICS_RESOURCE MLR WHERE MLR.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MLR.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MLR.RESOURCE_GROUP_CD = #{resourceGroupCd, jdbcType=VARCHAR}
AND (UPPER(MLR.RESOURCE_CD) LIKE '%' || UPPER(#{resourceCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MLR.RESOURCE_NM) LIKE '%' || UPPER(#{resourceCdNm, jdbcType=VARCHAR}) || '%')
AND MLR.USE_YN = #{useYn, jdbcType=VARCHAR}
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_LOGISTICS_RESOURCE


      (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
         AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
         AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
      )

insert.foreach:

INTO MOM_PROC_LOGISTICS_RESOURCE( COMPANY_CD
                                , DIVISION_CD
                                , RESOURCE_CD  
                                , RESOURCE_NM  
                                , RESOURCE_GROUP_CD 
                                , USE_YN
                                , DESCRIPTION
                                , DEL_YN
                                , VAL_MSG
                                , MULTI_MSG
                                , CREATE_BY
                                , CREATE_DATE
                                )
VALUES                         ( #{item.companyCd, jdbcType=VARCHAR}
                               , #{item.divisionCd, jdbcType=VARCHAR}
                               , #{item.resourceCd, jdbcType=VARCHAR}
                               , #{item.resourceNm, jdbcType=VARCHAR}
                               , #{item.resourceGroupCd, jdbcType=VARCHAR}
                               , #{item.useYn, jdbcType=VARCHAR}
                               , #{item.description, jdbcType=VARCHAR}
                               , #{item.delYn, jdbcType=VARCHAR}
                               , #{item.valMsg, jdbcType=VARCHAR}
                               , #{item.multiMsg, jdbcType=VARCHAR}
                               , #{item.userId, jdbcType=VARCHAR}
                               , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                               )

mapper.select:

{ CALL MOM_MASTER_INFO_PKG.SP_UPSERT_LOGISTICS_RESOURCE( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                       , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                       , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                       , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                                       , #{actionMode,  jdbcType=VARCHAR, mode=IN}        
                                                       , #{userId,      jdbcType=VARCHAR, mode=IN} )}

SELECT NVL(VAL_MSG,'PASS') AS VAL_MSG
     , MULTI_MSG      
FROM   MOM_PROC_LOGISTICS_RESOURCE
WHERE  COMPANY_CD  = #{companyCd , jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd , jdbcType=VARCHAR}
AND    CREATE_BY    = #{userId , jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_LOGISTICS_RESOURCE


      (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
         AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
         AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
      )

insert.foreach:

INTO MOM_PROC_LOGISTICS_RESOURCE( COMPANY_CD
                                , DIVISION_CD
                                , RESOURCE_CD  
                                , RESOURCE_NM  
                                , RESOURCE_GROUP_CD 
                                , USE_YN
                                , DESCRIPTION
                                , DEL_YN
                                , VAL_MSG
                                , MULTI_MSG
                                , CREATE_BY
                                , CREATE_DATE
                                )
VALUES                          ( #{item.companyCd, jdbcType=VARCHAR}
                                , #{item.divisionCd, jdbcType=VARCHAR}
                                , #{item.resourceCd, jdbcType=VARCHAR}
                                , #{item.resourceNm, jdbcType=VARCHAR}
                                , #{item.resourceGroupCd, jdbcType=VARCHAR}
                                , #{item.useYn, jdbcType=VARCHAR}
                                , #{item.description, jdbcType=VARCHAR}
                                , 'N'
                                , #{item.valMsg, jdbcType=VARCHAR}
                                , #{item.multiMsg, jdbcType=VARCHAR}
                                , #{item.userId, jdbcType=VARCHAR}
                                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                                )

mapper.select:

{ CALL MOM_MASTER_INFO_PKG.SP_UPSERT_LOGISTICS_RESOURCE( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                       , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                       , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                       , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                                       , #{actionMode,  jdbcType=VARCHAR, mode=IN}        
                                                       , #{userId,      jdbcType=VARCHAR, mode=IN} )}
