mapper.select:
SELECT MPM.COMPANY_CD , MPM.DIVISION_CD , MPM.PO_NO , MPM.PO_REFERENCE_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0005', MPM.PO_REFERENCE_TYPE, 'KR') FROM DUAL) AS PO_REFERENCE_TYPE_NM , MPM.PO_TITLE , MPM.PO_CATEGORY , (SELECT MOM_COMMON_PKG.FN_GET_PO_TYPE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.PO_CATEGORY) FROM DUAL) AS PO_CATEGORY_NM , MPM.VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPM.COMPANY_CD,MPM.DIVISION_CD,MPM.VENDOR_CD) FROM DUAL) AS VENDOR_NM , MPM.PO_DUE_DATE , MPM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'SM0017', MPM.CURRENCY_CD, 'KR') FROM DUAL) AS CURRENCY_NM , MPM.EXCHANGE_RATE , MPM.PO_AMOUNT , MPM.PAYMENT_METHOD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0006', MPM.PAYMENT_METHOD, 'KR') FROM DUAL) AS PAYMENT_METHOD_NM , MPM.PRICE_TERMS , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0007', MPM.PRICE_TERMS, 'KR') FROM DUAL) AS PRICE_TERMS_NM , MPM.PAYMENT_TERMS , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0008', MPM.PAYMENT_TERMS, 'KR') FROM DUAL) AS PAYMENT_TERMS_NM , MPM.SHIPPING_METHOD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0009', MPM.SHIPPING_METHOD, 'KR') FROM DUAL) AS SHIPPING_METHOD_NM , MPM.PO_STATE AS STATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0010', MPM.PO_STATE, 'KR') FROM DUAL) AS STATE_NM , TO_CHAR(MPM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , MPM.CREATE_BY AS CREATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.CREATE_BY) FROM DUAL ) AS CREATE_USER_NM , MPM.CREATE_DEPT_CD , (SELECT MOM_COMMON_PKG.FN_GET_DEPARTMENT_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.CREATE_DEPT_CD) FROM DUAL) AS CREATE_DEPT_NM , MPM.DO_INVOICE_YN , MPM.DESCRIPTION FROM MOM_PO_MST MPM WHERE MPM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MPM.PR_NO) LIKE '%' || UPPER(#{prNo, jdbcType=VARCHAR}) || '%')
AND MPM.CREATE_DEPT_CD = #{poDeptCd, jdbcType=VARCHAR}
AND (UPPER(MPM.PO_TITLE) LIKE '%' || UPPER(#{poTitle, jdbcType=VARCHAR}) || '%')
AND MPM.CREATE_DEPT_CD = #{createDeptCd, jdbcType=VARCHAR}
AND MPM.VENDOR_CD = #{vendorCd, jdbcType=VARCHAR}
AND MPM.PO_CATEGORY = #{poCategory, jdbcType=VARCHAR}
ORDER BY MPM.PO_NO

mapper.delete:
DELETE FROM MOM_PROC_PO_MST


 (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
    AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
    AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
 )

insert.foreach:

INTO MOM_PROC_PO_MST (
             COMPANY_CD
           , DIVISION_CD
           , PO_NO
           , PO_REFERENCE_TYPE
           , PO_TITLE
           , PO_CATEGORY
           , VENDOR_CD
           , PO_DUE_DATE
           , DO_INVOICE_YN
           , CURRENCY_CD
           , PAYMENT_METHOD
           , PRICE_TERMS
           , PAYMENT_TERMS
           , SHIPPING_METHOD
           , DESCRIPTION
           , DEL_YN
           , VAL_MSG
           , MULTI_MSG
           , CREATE_BY
           , CREATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.poNo, jdbcType=VARCHAR}
           , #{item.poReferenceType, jdbcType=VARCHAR}
           , #{item.poTitle, jdbcType=VARCHAR}
           , #{item.poCategory, jdbcType=VARCHAR}
           , #{item.vendorCd, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
           , #{item.doInvoiceYn, jdbcType=VARCHAR}
           , #{item.currencyCd, jdbcType=VARCHAR}
           , #{item.paymentMethod, jdbcType=VARCHAR}
           , #{item.priceTerms, jdbcType=VARCHAR}
           , #{item.paymentTerms, jdbcType=VARCHAR}
           , #{item.shippingMethod, jdbcType=VARCHAR}
           , #{item.description, jdbcType=VARCHAR}
           , 'N'
           , #{item.valMsg, jdbcType=VARCHAR}
           , #{item.multiMsg, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

mapper.select:

          { CALL MOM_MATERIAL_PKG.sp_upsert_po_mst(     		#{p_err_code,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,   javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd,  jdbcType=VARCHAR, mode=IN}											  
					                                          , #{userId,      jdbcType=VARCHAR, mode=IN} )}

