mapper.select:
SELECT MPM.PO_NO , MPD.PO_SEQ , MPD.ITEM_ID , TO_CHAR(MPM.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE , MPM.CONFIRM_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.CONFIRM_USER_NO) FROM DUAL ) AS CONFIRM_USER_NM , TO_CHAR(MPM.PO_DUE_DATE ,'YYYY-MM-DD') AS PO_DUE_DATE , MPD.CONFIRM_QTY , MPD.PROGRESS_QTY , MPD.CANCEL_QTY AS CANCEL_QTY , '' AS INPUT_CANCEL_QTY , MPD.CONFIRM_QTY - NVL(MPD.PROGRESS_QTY,0) - NVL(MPD.CANCEL_QTY,0) AS REMAIN_QTY , MPM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'SM0017', MPM.CURRENCY_CD, 'KR') FROM DUAL) AS CURRENCY_NM , MPM.EXCHANGE_RATE , MPD.UNIT_PRICE , MPD.AMOUNT , MPD.LOCAL_AMOUNT , MPD.DEPARTURE_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MPD.COMPANY_CD,MPD.DIVISION_CD,MPD.DEPARTURE_LOCATION_CD) FROM DUAL) AS DEPARTURE_LOCATION_NM , MPD.TEST_REPORT_FLAG , MPD.IQC_FLAG , MPD.LOT_MANAGEMENT_YN , MPD.PRICE_SYNC_YN , MPD.PO_OVER_RECEIPT_RATE , MI.MAKER , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0007', MI.MAKER, 'KR') FROM DUAL) AS MAKER_NM , MPM.VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPM.COMPANY_CD,MPM.DIVISION_CD,MPM.VENDOR_CD) FROM DUAL) AS VENDOR_NM , MPM.PO_REFERENCE_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0005', MPM.PO_REFERENCE_TYPE, 'KR') FROM DUAL) AS PO_REFERENCE_TYPE_NM , MPM.PO_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_PO_TYPE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.PO_TYPE) FROM DUAL) AS PO_TYPE_NM , MI.PROCUREMENT_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0004', MI.PROCUREMENT_TYPE, 'KR') FROM DUAL) AS PROCUREMENT_TYPE_NM , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, 'KR') FROM DUAL) AS ITEM_TYPE_NM , MI.PURCHASE_UNIT , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.PURCHASE_UNIT) FROM DUAL) AS PURCHASE_UNIT_NM , MPD.CREATE_BY , MPD.CREATE_DATE , MPD.PURCHASE_ORDER_ID , MPD.PURCHASE_ORDER_ID AS KEY_ID FROM MOM_PO_DTL MPD , MOM_PO_MST MPM , MOM_ITEM MI WHERE MPD.COMPANY_CD = MPM.COMPANY_CD AND MPD.DIVISION_CD = MPM.DIVISION_CD AND MPD.PO_NO = MPM.PO_NO AND MPD.COMPANY_CD = MI.COMPANY_CD AND MPD.DIVISION_CD = MI.DIVISION_CD AND MPD.ITEM_ID = MI.ITEM_ID AND MPD.STATE = 'POF' -- 확정 AND MPM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPM.CONFIRM_DATE BETWEEN TO_DATE(#{confirmDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{confirmDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MPM.PO_NO) LIKE '%' || UPPER(#{poNo, jdbcType=VARCHAR}) || '%')
AND MPD.CREATE_BY = #{poUserNo, jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND MPM.VENDOR_CD = #{vendorCd, jdbcType=VARCHAR}
ORDER BY MPD.PO_NO , MPD.PO_SEQ

mapper.delete:
DELETE FROM MOM_PROC_PO_CLOSE


 (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
    AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
    AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
 )

insert.foreach:

          INTO   MOM_PROC_PO_CLOSE (
			                  COMPANY_CD
			                , DIVISION_CD
			                , ACTION_TYPE
			                , PURCHASE_ORDER_ID
			                , CANCEL_QTY
			                , VAL_MSG
			                , MULTI_MSG
			                , DESCRIPTION
			                , CREATE_BY
			                , CREATE_DATE )
			      VALUES 
			              (   #{item.companyCd, jdbcType=VARCHAR}
			                , #{item.divisionCd, jdbcType=VARCHAR}
			                , #{item.actionType, jdbcType=VARCHAR}
			                , #{item.purchaseOrderId, jdbcType=VARCHAR}
			                , NVL(#{item.inputCancelQty, jdbcType=NUMERIC},0)
			                , #{item.valMsg, jdbcType=VARCHAR}
			                , #{item.multiMsg, jdbcType=VARCHAR}
			                , #{item.description, jdbcType=VARCHAR}
			                , #{item.userId, jdbcType=VARCHAR}
			                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
			              )

mapper.select:

          { CALL MOM_MATERIAL_PKG.sp_po_close(     		#{p_err_code,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,   javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd,  jdbcType=VARCHAR, mode=IN}											  
					                                          , #{userId,      jdbcType=VARCHAR, mode=IN} )}

mapper.delete:
DELETE FROM MOM_PROC_PO_CLOSE


 (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
    AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
    AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
 )

insert.foreach:

          INTO   MOM_PROC_PO_CLOSE (
			                  COMPANY_CD
			                , DIVISION_CD
			                , ACTION_TYPE
			                , PURCHASE_ORDER_ID
			                , CANCEL_QTY
			                , VAL_MSG
			                , MULTI_MSG
			                , DESCRIPTION
			                , CREATE_BY
			                , CREATE_DATE )
			      VALUES 
			              (   #{item.companyCd, jdbcType=VARCHAR}
			                , #{item.divisionCd, jdbcType=VARCHAR}
			                , #{item.actionType, jdbcType=VARCHAR}
			                , #{item.purchaseOrderId, jdbcType=VARCHAR}
			                , NVL(#{item.inputCancelQty, jdbcType=NUMERIC},0)
			                , #{item.valMsg, jdbcType=VARCHAR}
			                , #{item.multiMsg, jdbcType=VARCHAR}
			                , #{item.description, jdbcType=VARCHAR}
			                , #{item.userId, jdbcType=VARCHAR}
			                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
			              )

