mapper.select:
SELECT MPIM.COMPANY_CD , MPIM.DIVISION_CD , MPIM.INOUT_NO AS RECEIVE_NO , MPIM.INOUT_NO AS KEY_ID , MPIM.BL_NO , MPIM.IP_NO , MPIM.LC_NO , MPIM.INOUT_TYPE , MPIM.IP_CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0017', MPIM.IP_CURRENCY_CD, 'KR') FROM DUAL) AS IP_CURRENCY_NM , MPIM.IP_EXCHANGE_RATE , TO_CHAR(MPIM.CC_DATE ,'YYYY-MM-DD') AS CC_DATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0012', MPIM.INOUT_TYPE, 'KR') FROM DUAL) AS INOUT_TYPE_NM , MPIM.TITLE AS RECEIVE_TITLE , MPIM.VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.VENDOR_CD) FROM DUAL) AS VENDOR_NM , MPIM.DEPARTURE_VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.DEPARTURE_VENDOR_CD) FROM DUAL) AS DEPARTURE_VENDOR_NM , MPIM.DEPARTURE_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.DEPARTURE_LOCATION_CD) FROM DUAL) AS DEPARTURE_LOCATION_NM , MPIM.IQC_FLAG , MPIM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0017', MPIM.CURRENCY_CD, 'KR') FROM DUAL) AS CURRENCY_NM , MPIM.EXCHANGE_RATE , TO_CHAR(MPIM.DEPARTURE_DATE ,'YYYY-MM-DD') AS DEPARTURE_DATE , MPIM.AMOUNT AS DEPARTURE_AMOUNT , MPIM.VENDOR_INVOICE , MPIM.DESCRIPTION , MPIM.CREATE_BY AS CREATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.CREATE_BY) FROM DUAL ) AS CREATE_USER_NM , TO_CHAR(MPIM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE FROM MOM_PURCHASE_INOUT_MST MPIM WHERE MPIM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPIM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPIM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MPIM.STATE = 'PIG' -- 등록 AND MPIM.INOUT_TYPE = 'RO' --외자거래
AND UPPER(MPIM.INOUT_NO) LIKE '%' || UPPER(#{receiveNo, jdbcType=VARCHAR}) || '%'
AND MPIM.VENDOR_CD = #{vendorCd, jdbcType=VARCHAR}
AND UPPER(MPIM.TITLE) LIKE '%' || UPPER(#{receiveTitle, jdbcType=VARCHAR}) || '%'
AND MPIM.CURRENCY_CD = #{currencyCd, jdbcType=VARCHAR}
AND MPIM.IQC_FLAG = #{iqcFlag, jdbcType=VARCHAR}
ORDER BY MPIM.INOUT_NO

mapper.delete:
