mapper.select:
SELECT MPIM.COMPANY_CD , MPIM.DIVISION_CD , MPIM.INOUT_NO , MPIM.INOUT_TYPE --, (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0012', MPIM.CURRENCY_CD, 'KR') FROM DUAL) AS INOUT_TYPE_NM , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0012', MPIM.INOUT_TYPE, 'KR') FROM DUAL) AS INOUT_TYPE_NM , MPIM.TITLE AS INOUT_TITLE , MPIM.VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.VENDOR_CD) FROM DUAL) AS VENDOR_NM , MPIM.DEPARTURE_VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.DEPARTURE_VENDOR_CD) FROM DUAL) AS DEPARTURE_VENDOR_NM , MPIM.DEPARTURE_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.DEPARTURE_LOCATION_CD) FROM DUAL) AS DEPARTURE_LOCATION_NM , TO_CHAR(MPIM.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE , MPIM.BL_NO , MPIM.IP_NO , MPIM.IP_CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0017', MPIM.IP_CURRENCY_CD, 'KR') FROM DUAL) AS IP_CURRENCY_NM , MPIM.IP_EXCHANGE_RATE , TO_CHAR(MPIM.CC_DATE ,'YYYY-MM-DD') AS CC_DATE , MPIM.LC_NO , MPIM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0017', MPIM.CURRENCY_CD, 'KR') FROM DUAL) AS CURRENCY_NM , MPIM.EXCHANGE_RATE , MPIM.AMOUNT AS INOUT_AMOUNT , TO_CHAR(MPIM.DEPARTURE_DATE ,'YYYY-MM-DD') AS DEPARTURE_DATE , MPIM.VENDOR_INVOICE , MPIM.FREE_YN AS FREE_YN --, (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0015', MPIM.REASON_CD, 'KR') FROM DUAL) AS FREE_YN_NM , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0015', MPIM.FREE_YN , 'KR') FROM DUAL) AS FREE_YN_NM , MPIM.REASON_CD AS EXP_RCV_REASON_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0016', MPIM.REASON_CD, 'KR') FROM DUAL) AS EXP_RCV_REASON_NM , MPIM.DESCRIPTION , MPIM.CREATE_BY AS CREATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.CREATE_BY) FROM DUAL ) AS CREATE_USER_NM , TO_CHAR(MPIM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , MPIM.CONFIRM_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.CONFIRM_USER_NO) FROM DUAL ) AS CONFIRM_USER_NM FROM MOM_PURCHASE_INOUT_MST MPIM WHERE MPIM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPIM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPIM.CONFIRM_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MPIM.INOUT_NO) LIKE '%' || UPPER(#{inoutNo, jdbcType=VARCHAR}) || '%')
AND MPM.VENDOR_CD = #{vendorCd, jdbcType=VARCHAR}
AND MPIM.INOUT_TYPE = #{inoutType, jdbcType=VARCHAR}
AND (UPPER(MPM.PO_TITLE) LIKE '%' || UPPER(#{inoutTitle, jdbcType=VARCHAR}) || '%')
AND MPIM.INOUT_TYPE = #{departureLocationCd, jdbcType=VARCHAR}
AND MPIM.STATE = 'PIF' -- 확정 AND MPIM.IQC_FLAG = 'Y' ORDER BY MPIM.INOUT_NO

mapper.delete:
DELETE FROM MOM_PROC_PO_MST


 (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
    AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
    AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
 )

insert.foreach:

INTO MOM_PROC_PO_MST (
             COMPANY_CD
           , DIVISION_CD
           , PO_NO
           , PO_REFERENCE_TYPE
           , PO_TITLE
           , PO_TYPE
           , VENDOR_CD
           , PO_DUE_DATE
           , DO_INVOICE_YN
           , CURRENCY_CD
           , PAYMENT_METHOD
           , PRICE_TERMS
           , PAYMENT_TERMS
           , SHIPPING_METHOD
           , DESCRIPTION
           , DEL_YN
           , VAL_MSG
           , MULTI_MSG
           , CREATE_BY
           , CREATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.poNo, jdbcType=VARCHAR}
           , #{item.poReferenceType, jdbcType=VARCHAR}
           , #{item.poTitle, jdbcType=VARCHAR}
           , #{item.poCategory, jdbcType=VARCHAR}
           , #{item.partnerCd, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
           , #{item.doInvoiceYn, jdbcType=VARCHAR}
           , #{item.currencyCd, jdbcType=VARCHAR}
           , #{item.paymentMethod, jdbcType=VARCHAR}
           , #{item.priceTerms, jdbcType=VARCHAR}
           , #{item.paymentTerms, jdbcType=VARCHAR}
           , #{item.shippingMethod, jdbcType=VARCHAR}
           , #{item.description, jdbcType=VARCHAR}
           , 'N'
           , #{item.valMsg, jdbcType=VARCHAR}
           , #{item.multiMsg, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

mapper.select:

          { CALL MOM_MATERIAL_PKG.sp_upsert_po_mst(     		#{p_err_code,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,   javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd,  jdbcType=VARCHAR, mode=IN}											  
					                                          , #{userId,      jdbcType=VARCHAR, mode=IN} )}
