mapper.select:
SELECT MPIM.COMPANY_CD , MPIM.DIVISION_CD , MPIM.INOUT_NO AS RECEIVE_NO , MPIM.INOUT_NO AS KEY_ID , MPIM.INOUT_TYPE --, (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0012', MPIM.CURRENCY_CD, 'KR') FROM DUAL) AS INOUT_TYPE_NM , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0012', MPIM.INOUT_TYPE, 'KR') FROM DUAL) AS INOUT_TYPE_NM , MPIM.IO_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_IO_TYPE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.IO_TYPE) FROM DUAL) AS IO_TYPE_NM , MPIM.TITLE AS RECEIVE_TITLE , MPIM.VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.VENDOR_CD) FROM DUAL) AS VENDOR_NM , MPIM.DEPARTURE_VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.DEPARTURE_VENDOR_CD) FROM DUAL) AS DEPARTURE_VENDOR_NM , MPIM.DEPARTURE_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.DEPARTURE_LOCATION_CD) FROM DUAL) AS DEPARTURE_LOCATION_NM , MPIM.BL_NO , MPIM.IP_NO , MPIM.IP_CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0017', MPIM.IP_CURRENCY_CD, 'KR') FROM DUAL) AS IP_CURRENCY_NM , MPIM.IP_EXCHANGE_RATE , TO_CHAR(MPIM.CC_DATE ,'YYYY-MM-DD') AS CC_DATE , MPIM.LC_NO , MPIM.IQC_FLAG , MPIM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0017', MPIM.CURRENCY_CD, 'KR') FROM DUAL) AS CURRENCY_NM , MPIM.EXCHANGE_RATE , MPIM.AMOUNT AS RECEIVE_AMOUNT , TO_CHAR(MPIM.DEPARTURE_DATE ,'YYYY-MM-DD') AS DEPARTURE_DATE , MPIM.VENDOR_INVOICE , MPIM.FREE_YN AS FREE_YN , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0015', MPIM.FREE_YN, 'KR') FROM DUAL) AS FREE_YN_NM , MPIM.REASON_CD AS EXP_RCV_REASON_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0016', MPIM.REASON_CD, 'KR') FROM DUAL) AS EXP_RCV_REASON_NM , MPIM.DESCRIPTION , MPIM.CREATE_BY AS CREATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.CREATE_BY) FROM DUAL ) AS CREATE_USER_NM , TO_CHAR(MPIM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE FROM MOM_PURCHASE_INOUT_MST MPIM WHERE MPIM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPIM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPIM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MPIM.INOUT_NO) LIKE '%' || UPPER(#{receiveNo, jdbcType=VARCHAR}) || '%')
AND MPIM.VENDOR_CD = #{vendorCd, jdbcType=VARCHAR}
AND (UPPER(MPIM.TITLE) LIKE '%' || UPPER(#{receiveTitle, jdbcType=VARCHAR}) || '%')
AND MPIM.DEPARTURE_LOCATION_CD = #{departureLocationCd, jdbcType=VARCHAR}
AND MPIM.IQC_FLAG = #{iqcFlag, jdbcType=VARCHAR}
AND EXISTS ( SELECT '*' FROM MOM_PURCHASE_INOUT_DTL MPID WHERE MPIM.COMPANY_CD = MPID.COMPANY_CD AND MPIM.DIVISION_CD = MPID.DIVISION_CD AND MPIM.INOUT_NO = MPID.INOUT_NO AND MPID.STATE IN ('PIWR', 'NCPC') ) ORDER BY MPIM.INOUT_NO

mapper.delete:
DELETE FROM MOM_PROC_PURCHASE_GR


 (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
    AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
    AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
 )

insert.foreach:

 INTO MOM_PROC_PURCHASE_GR (
            COMPANY_CD
          , DIVISION_CD
          , INOUT_TYPE
          , INOUT_NO
          , SEQ
          , PURCHASE_INOUT_ID
          , PURCHASE_ORDER_ID
          , VENDOR_CD
          , LOCATION_CD
          , ITEM_ID
          , IO_TYPE
          , INOUT_QTY
          , INOUT_UNIT
          , UNIT_PRICE
          , AMOUNT
          , LOCAL_AMOUNT
          , EXCHANGE_RATE
          , CURRENCY_CD
          , LOT_ID
          , DESCRIPTION
          , VAL_MSG
          , MULTI_MSG
          , CREATE_BY
          , CREATE_DATE )
VALUES 
        (   #{item.companyCd, jdbcType=VARCHAR}
          , #{item.divisionCd, jdbcType=VARCHAR}
          , #{item.inoutType, jdbcType=VARCHAR}
          , #{item.receiveNo, jdbcType=VARCHAR}
          , #{item.seq, jdbcType=NUMERIC}
          , #{item.purchaseInoutId, jdbcType=VARCHAR}
          , #{item.purchaseOrderId, jdbcType=VARCHAR}
          , #{item.vendorCd, jdbcType=VARCHAR}
          , #{item.locationCd, jdbcType=VARCHAR}
          , #{item.itemId, jdbcType=VARCHAR}
          , #{item.ioType, jdbcType=VARCHAR}
          , #{item.receiveWaitQty, jdbcType=NUMERIC}
          , #{item.purchaseUnit, jdbcType=VARCHAR}
          , #{item.unitPrice, jdbcType=NUMERIC}
          , #{item.amount, jdbcType=NUMERIC}
          , #{item.localAmount, jdbcType=NUMERIC}
          , #{item.exchangeRate, jdbcType=NUMERIC}
          , #{item.currencyCd, jdbcType=VARCHAR}
          , #{item.lotId, jdbcType=VARCHAR}
          , #{item.description, jdbcType=VARCHAR}
          , #{item.valMsg, jdbcType=VARCHAR}
          , #{item.multiMsg, jdbcType=VARCHAR}
          , #{item.userId, jdbcType=VARCHAR}
          , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
        )

mapper.select:

          { CALL MOM_MATERIAL_PKG.sp_purchase_goods_receipt(    #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,   javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd,  jdbcType=VARCHAR, mode=IN}											  
					                                          , #{userId,      jdbcType=VARCHAR, mode=IN} )}
