mapper.select:
SELECT MPIM.COMPANY_CD , MPIM.DIVISION_CD , MPIM.INOUT_NO AS RETURN_NO -- 반품서번호 , MPIM.INOUT_NO AS KEY_ID , MPIM.INOUT_TYPE -- 입출고유형 , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0012', MPIM.INOUT_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS INOUT_TYPE_NM , MPIM.IO_TYPE , (SELECT MOM_COMMON_PKG.fn_get_io_type_name(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.IO_TYPE) FROM DUAL) AS IO_TYPE_NM , MPIM.TITLE AS RETURN_TITLE , MPIM.VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.VENDOR_CD) FROM DUAL) AS VENDOR_NM , MPIM.DEPARTURE_LOCATION_CD AS ISSUE_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD,MPIM.DEPARTURE_LOCATION_CD) FROM DUAL) AS ISSUE_LOCATION_NM , MPIM.IQC_FLAG , MPIM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0017', MPIM.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MPIM.EXCHANGE_RATE , MPIM.AMOUNT AS RETURN_AMOUNT , MPIM.STATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'MM0013', MPIM.STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STATE_NM , MPIM.CREATE_BY AS CREATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.CREATE_BY) FROM DUAL ) AS CREATE_USER_NM , TO_CHAR(MPIM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , MPIM.DESCRIPTION FROM MOM_PURCHASE_INOUT_MST MPIM WHERE MPIM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPIM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPIM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MPIM.INOUT_NO) LIKE '%' || UPPER(#{returnNo, jdbcType=VARCHAR}) || '%')
AND MPIM.VENDOR_CD = #{vendorCd, jdbcType=VARCHAR}
AND (UPPER(MPIM.TITLE) LIKE '%' || UPPER(#{returnTitle, jdbcType=VARCHAR}) || '%')
AND MPIM.IO_TYPE = #{ioType, jdbcType=VARCHAR}
AND MPIM.IO_TYPE = #{iqcFlag, jdbcType=VARCHAR}
AND MPIM.STATE = 'PIG' -- 등록 AND MPIM.INOUT_TYPE = 'TD' ORDER BY MPIM.INOUT_NO

mapper.delete:
