mapper.select:
SELECT A.* FROM (
SELECT MSO.COMPANY_CD , MSO.DIVISION_CD , MSO.SO_NO , MSO.SO_SEQ , MSO.SO_TYPE , MSO.SO_TYPE_NM , MSO.CUSTOMER_CD , MSO.CUSTOMER_NM , MSO.ITEM_ID , MSO.ITEM_NM , MSO.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MSO.COMPANY_CD, MSO.DIVISION_CD, 'MD0002', MSO.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MSO.ITEM_SPEC , MSO.PREV_QTY AS BC_ORDER_QTY , MSO.ORDER_QTY AS AC_ORDER_QTY , MSO.SALES_ORDER_ID , MSO.UPDATE_BY , MSO.UPDATE_BY_NM , MSO.UPDATE_DATE , MSO.SALES_ORDER_ID AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MSO.COMPANY_CD,MSO.DIVISION_CD ORDER BY MSO.SO_NO, MSO.SO_SEQ ) AS ROW_COUNT FROM MOM_SALES_ORDER_V MSO WHERE MSO.COMPANY_CD = 'CALCES' AND MSO.DIVISION_CD = 'DEV1' AND MSO.CHANGE_YN = 'Y'
AND (UPPER(MSO.SO_NO) LIKE '%' || UPPER(#{soNo, jdbcType=VARCHAR}) || '%' )
AND MSO.SO_TYPE IN (${soType})
AND (UPPER(MSO.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MSO.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
) A WHERE ROW_COUNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}


{ CALL MOM_DEMAND_PKG.SP_DEMAND_PLAN( #{p_err_code, jdbcType=VARCHAR, mode=OUT}
                                    , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
                                    , #{companyCd,  jdbcType=VARCHAR, mode=IN}
                                    , #{divisionCd, jdbcType=VARCHAR, mode=IN}											  
                                    , #{userId,     jdbcType=VARCHAR, mode=IN} )}
