mapper.select:
WITH C_SO AS ( SELECT MSC.COMPANY_CD , MSC.DIVISION_CD , MSC.CHANGE_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MSC.COMPANY_CD, MSC.DIVISION_CD, 'SM0020', MSC.CHANGE_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CHANGE_TYPE_NM , MSC.CHANGE_DATE_YN , MSC.CHANGE_QTY_YN , TO_CHAR( MSC.BC_SHIPPING_DATE ,'YYYY-MM-DD') AS BC_SHIPPING_DATE , MSC.BC_ORDER_QTY , TO_CHAR( MSC.AC_SHIPPING_DATE ,'YYYY-MM-DD') AS AC_SHIPPING_DATE , MSC.AC_ORDER_QTY , MSC.SALES_ORDER_ID , MSC.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MSC.COMPANY_CD, MSC.DIVISION_CD, MSC.UPDATE_BY) FROM DUAL ) AS UPDATE_BY_NM , TO_CHAR(MSC.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE FROM MOM_SO_CHANGE MSC WHERE MSC.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MSC.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MSC.APPLY_FLAG = 'N'
AND MSC.CHANGE_DATE_YN = #{changeDateYn, jdbcType=VARCHAR}
AND MSC.CHANGE_QTY_YN = #{changeQtyYn, jdbcType=VARCHAR}
)
SELECT A.* FROM (
SELECT MSC.COMPANY_CD , MSC.DIVISION_CD , MSC.CHANGE_TYPE , MSC.CHANGE_TYPE_NM , MSO.SO_NO , MSO.SO_SEQ , MSO.SO_TYPE , MSO.SO_TYPE_NM , MSO.CUSTOMER_CD , MSO.CUSTOMER_NM , MSO.ITEM_ID , MSO.ITEM_NM , MSO.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MSC.COMPANY_CD, MSC.DIVISION_CD, 'MD0002', MSO.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MSO.ITEM_SPEC , MSC.CHANGE_DATE_YN , MSC.CHANGE_QTY_YN , MSC.BC_SHIPPING_DATE , MSC.BC_ORDER_QTY , MSC.AC_SHIPPING_DATE , MSC.AC_ORDER_QTY , MSC.SALES_ORDER_ID , MSC.UPDATE_BY , MSC.UPDATE_BY_NM , MSC.UPDATE_DATE , ROWIDTOCHAR(MSO.ROWID) AS GRID_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MSO.COMPANY_CD,MSO.DIVISION_CD ORDER BY MSO.SO_NO, MSO.SO_SEQ ) AS KEY_ID FROM C_SO MSC , MOM_SALES_ORDER_V MSO WHERE MSC.COMPANY_CD = MSO.COMPANY_CD AND MSC.DIVISION_CD = MSO.DIVISION_CD AND MSC.SALES_ORDER_ID = MSO.SALES_ORDER_ID
AND (UPPER(MSO.SO_NO) LIKE '%' || UPPER(#{soNo, jdbcType=VARCHAR}) || '%' )
AND MSO.SO_TYPE IN (${soType})
AND (UPPER(MSO.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MSO.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
) A WHERE KEY_ID BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}


{ CALL MOM_DEMAND_PKG.SP_DEMAND_PLAN( #{p_err_code, jdbcType=VARCHAR, mode=OUT}
                                    , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
                                    , #{companyCd,  jdbcType=VARCHAR, mode=IN}
                                    , #{divisionCd, jdbcType=VARCHAR, mode=IN}											  
                                    , #{userId,     jdbcType=VARCHAR, mode=IN} )}
