mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MWC.WORK_GROUP_CD , MWG.WORK_GROUP_NM , MPO.WORK_CENTER_CD , MWC.WORK_CENTER_NM , MPO.ITEM_ID , MPO.WO_TYPE , MPO.MANUAL_YN , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPO.COMPANY_CD, MPO.DIVISION_CD, 'PP0002', MPO.WO_TYPE, 'KR') FROM DUAL) AS WO_TYPE_NM , MI.ITEM_NM , MI.ITEM_SPEC , MPO.WR_STATE AS STATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPO.COMPANY_CD, MPO.DIVISION_CD, 'PP0004', MPO.WR_STATE, 'KR') FROM DUAL) AS STATE_NM , TO_CHAR(MPO.PLAN_DATE ,'YYYY-MM-DD') AS PLAN_DATE , TO_CHAR(MPO.PLAN_START_TIME ,'YYYY-MM-DD') AS PLAN_START_DATE , TO_CHAR(MPO.PLAN_END_TIME ,'YYYY-MM-DD') AS PLAN_END_DATE , MPO.PLAN_QTY , MPO.CONFIRM_QTY , MPO.PRIORITY , MPO.ROUTING_ID , (SELECT MOM_COMMON_PKG.fn_get_routing_name(MPO.COMPANY_CD, MPO.DIVISION_CD, MPO.ROUTING_ID) FROM DUAL) AS ROUTING_NM , MPO.PRODUCT_ORDER_ID AS WORK_ORDER_ID , MPO.PRODUCT_ORDER_ID AS KEY_ID , MPO.DEMAND_ID , MPO.OUTSOURCING_YN , MPO.BOR_ID , MPO.UPDATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPO.COMPANY_CD, MPO.DIVISION_CD, MPO.UPDATE_BY) FROM DUAL ) AS UPDATEBYNM , TO_CHAR(MPO.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE FROM MOM_COM_DIV_V MCD , MOM_PRODUCT_ORDER MPO , MOM_ITEM MI , MOM_WORK_CENTER MWC , MOM_WORK_GROUP MWG WHERE MCD.COMPANY_CD = MPO.COMPANY_CD AND MCD.DIVISION_CD = MPO.DIVISION_CD AND MPO.COMPANY_CD = MI.COMPANY_CD AND MPO.DIVISION_CD = MI.DIVISION_CD AND MPO.ITEM_ID = MI.ITEM_ID AND MPO.COMPANY_CD = MWC.COMPANY_CD AND MPO.DIVISION_CD = MWC.DIVISION_CD AND MPO.WORK_CENTER_CD = MWC.WORK_CENTER_CD AND MWC.COMPANY_CD = MWG.COMPANY_CD AND MWC.DIVISION_CD = MWG.DIVISION_CD AND MWC.WORK_GROUP_CD = MWG.WORK_GROUP_CD AND MPO.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPO.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPO.PLAN_START_TIME BETWEEN TO_DATE(#{planDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{planDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MPO.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MPO.PRODUCT_ORDER_ID) LIKE '%' || UPPER(#{workOrderId, jdbcType=VARCHAR}) || '%')
AND MWC.WORK_GROUP_CD = #{workGroupCd, jdbcType=VARCHAR}
AND MPO.WORK_CENTER_CD = #{workCenterCd, jdbcType=VARCHAR}
AND MPO.WR_STATE IN (${state})
AND MPO.OUTSOURCING_YN = #{outsourcingYn, jdbcType=VARCHAR}
ORDER BY MWG.WORK_GROUP_NM , MWC.WORK_CENTER_NM , MPO.PLAN_START_TIME , MPO.PRIORITY , MPO.ITEM_ID

update.foreach:
UPDATE MOM_PRODUCT_ORDER
SET    WORK_CENTER_CD = #{item.workCenterCd, jdbcType=VARCHAR}
     , ROUTING_ID = #{item.routingId, jdbcType=VARCHAR}       
     , CONFIRM_QTY = #{item.confirmQty, jdbcType=NUMERIC}         
     , PRIORITY = #{item.priority, jdbcType=NUMERIC}       
     , BOR_ID = #{item.borId, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    PRODUCT_ORDER_ID = #{item.workOrderId, jdbcType=VARCHAR}

insert.foreach:

INTO MOM_PROC_WORK_ORDER (
             COMPANY_CD
           , DIVISION_CD
           , WORK_ORDER_ID
           , WO_TYPE
           , BOR_ID
           , WO_QTY
           , WO_START_TIME
           , WO_END_TIME
           , PRIORITY
           , REASON_CD
           , MANUAL_YN
           , DESCRIPTION
           , VAL_MSG
           , MULTI_MSG
           , CREATE_BY
           , CREATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.workOrderId, jdbcType=VARCHAR}
           , #{item.woType, jdbcType=VARCHAR}
           , #{item.borId, jdbcType=VARCHAR}
           , #{item.confirmQty, jdbcType=NUMERIC}
           , TO_DATE(#{item.planStartDate, jdbcType=VARCHAR},'YYYY-MM-DD')
           , TO_DATE(#{item.planEndDate, jdbcType=VARCHAR},'YYYY-MM-DD')
           , #{item.priority, jdbcType=NUMERIC}
           , #{item.reasonCd, jdbcType=VARCHAR}
           , #{item.manualYn, jdbcType=VARCHAR}
           , #{item.description, jdbcType=VARCHAR}
           , #{item.valMsg, jdbcType=VARCHAR}
           , #{item.multiMsg, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

mapper.delete:
DELETE FROM MOM_PROC_WORK_ORDER


  (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
     AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
  )

mapper.select:

           { CALL MOM_WO_PKG.sp_confirm_work_order(             #{p_err_code, jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,  javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd, jdbcType=VARCHAR, mode=IN}											  
					                                          , #{userId,     jdbcType=VARCHAR, mode=IN} )}
