mapper.select:
	SELECT  CODE
         ,  CODE_NM AS LABEL
	FROM    MOM_CODE
	WHERE   DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
	AND     COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
	AND     GROUP_CD    = #{groupCd, jdbcType=VARCHAR}
	AND     USE_YN      = 'Y'
	ORDER BY SORT_NO

	  SELECT AUTH_GROUP_CD AS CODE 
	     ,   AUTH_GROUP_CD ||'('|| AUTH_GROUP_NM ||')' AS LABEL
      FROM   MOM_AUTH_GROUP
      WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
	  AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} 
      ORDER BY AUTH_GROUP_NM

SELECT MC.COMPANY_CD , MC.COMPANY_NM FROM MOM_COMPANY MC WHERE MC.USE_YN = 'Y'
AND MD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
ORDER BY COMPANY_NM

SELECT DIVISION_CD , DIVISION_NM FROM MOM_DIVISION_V WHERE 1=1
AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
ORDER BY DIVISION_NM
