									 , REPRESENTATIVE_NM_ENG
									 , USE_YN
									 , DESCRIPTION
									 , CREATE_BY
									 , CREATE_DATE
									 , UPDATE_BY
									 , UPDATE_DATE
		 							  )     
            VALUES(

						               #{item.companyCode,         jdbcType=VARCHAR}
						             , #{item.divisionCode,        jdbcType=VARCHAR}
						             , #{item.divisionNm,          jdbcType=VARCHAR}
