mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MU.UNIT_CATEGORY , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MU.COMPANY_CD,MU.DIVISION_CD, 'SM0016', MU.UNIT_CATEGORY, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS UNIT_CATEGORY_NM , MU.UNIT , MU.UNIT_NM , MU.USE_YN , MU.DESCRIPTION , MU.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MU.COMPANY_CD, MU.DIVISION_CD, MU.UPDATE_BY) FROM DUAL ) AS UPDATEBYNM , MU.UPDATE_DATE FROM MOM_COM_DIV_V MCD , MOM_UNIT MU WHERE MCD.COMPANY_CD = MU.COMPANY_CD AND MCD.DIVISION_CD = MU.DIVISION_CD AND MCD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MU.UNIT_CATEGORY IN (${unitCategory})
AND MU.UNIT IN (${unit})
AND MU.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MCD.COMPANY_NM , MCD.DIVISION_NM, MU.UNIT_NM

mapper.insert:
INSERT INTO MOM_UNIT (
            COMPANY_CD
          , DIVISION_CD
          , UNIT_CATEGORY
          , UNIT
          , UNIT_NM
          , USE_YN
          , DESCRIPTION
          , CREATE_BY
          , CREATE_DATE
          , UPDATE_BY
          , UPDATE_DATE )
VALUES 
        (   #{item.companyCd, jdbcType=VARCHAR}
          , #{item.divisionCd, jdbcType=VARCHAR}
          , #{item.unitCategory, jdbcType=VARCHAR}
          , #{item.unit, jdbcType=VARCHAR}
          , #{item.unitNm, jdbcType=VARCHAR}
          , #{item.useYn, jdbcType=VARCHAR}
          , #{item.description, jdbcType=VARCHAR}
          , #{item.userId, jdbcType=VARCHAR}
          , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
          , #{item.userId, jdbcType=VARCHAR}
          , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
        )

update.foreach:
UPDATE MOM_UNIT
SET    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     , DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     , UNIT_CATEGORY = #{item.unitCategory, jdbcType=VARCHAR}
     , UNIT = #{item.unit, jdbcType=VARCHAR}
     , UNIT_NM = #{item.unitNm, jdbcType=VARCHAR}
     , USE_YN = #{item.useYn, jdbcType=VARCHAR}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    UNIT = #{item.unit, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_UNIT


  (       COMPANY_CD = #{item.companyCd}
     AND  DIVISION_CD = #{item.divisionCd}
     AND  UNIT = #{item.unit}
  )
