mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MOC.IL_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_IL_TYPE_NAME(MOC.COMPANY_CD,MOC.DIVISION_CD, MOC.IL_TYPE) FROM DUAL) AS IL_TYPE_NM , MOC.IO_TYPE , MOC.IO_TYPE_NM , MOC.USE_YN , MOC.DESCRIPTION , MOC.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MOC.COMPANY_CD, MOC.DIVISION_CD, MOC.UPDATE_BY) FROM DUAL ) AS UPDATE_BY_NM , TO_CHAR(MOC.UPDATE_DATE,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , MOC.IO_TYPE || '_' || MOC.IL_TYPE AS KEY_ID FROM MOM_COM_DIV_V MCD , MOM_IO_CATEGORY MOC WHERE MCD.COMPANY_CD = MOC.COMPANY_CD AND MCD.DIVISION_CD = MOC.DIVISION_CD
AND MOC.IL_TYPE = #{ilType, jdbcType=VARCHAR})
AND MOC.IO_TYPE = #{ioType, jdbcType=VARCHAR}
AND MOC.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MCD.COMPANY_NM, MCD.DIVISION_NM, MOC.IO_TYPE_NM

insert.foreach:

INTO MOM_IO_CATEGORY (
             COMPANY_CD
           , DIVISION_CD
           , IO_TYPE
           , IO_TYPE_NM
           , IL_TYPE
           , USE_YN
           , DESCRIPTION
           , CREATE_BY
           , CREATE_DATE
           , UPDATE_BY
           , UPDATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.ioType, jdbcType=VARCHAR}
           , #{item.ioTypeNm, jdbcType=VARCHAR}
           , #{item.ilType, jdbcType=VARCHAR}
           , #{item.useYn, jdbcType=VARCHAR}
           , #{item.description, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

update.foreach:
UPDATE MOM_IO_CATEGORY
SET    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     , DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     , IO_TYPE = #{item.ioType, jdbcType=VARCHAR}
     , IO_TYPE_NM = #{item.ioTypeNm, jdbcType=VARCHAR}
     , IL_TYPE = #{item.ilType, jdbcType=VARCHAR}
     , USE_YN = #{item.useYn, jdbcType=VARCHAR}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    IO_TYPE = #{item.ioType, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_IO_CATEGORY


  (       COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     AND  IO_TYPE = #{item.ioType, jdbcType=VARCHAR}
  )
