mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MG.MENU_ID , (SELECT MOM_COMMON_PKG.FN_GET_DIVISION_MENU_NAME(MG.COMPANY_CD,MG.DIVISION_CD, MG.MENU_ID) FROM DUAL) AS MENU_NM , MG.GRID_ID , MG.GRID_NM , MG.USE_YN , MG.DESCRIPTION , MG.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MG.COMPANY_CD, MG.DIVISION_CD, MG.UPDATE_BY) FROM DUAL ) AS UPDATEBYNM , TO_CHAR(MG.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , MG.MENU_ID ||'_'|| MG.GRID_ID AS KEY_ID FROM MOM_COM_DIV_V MCD , MOM_GRID MG WHERE MCD.COMPANY_CD = MG.COMPANY_CD AND MCD.DIVISION_CD = MG.DIVISION_CD AND MCD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MG.MENU_ID = #{menuId, jdbcType=VARCHAR}
AND MG.GRID_ID = #{gridId, jdbcType=VARCHAR}
AND MG.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MCD.COMPANY_NM, MCD.DIVISION_NM, MG.MENU_ID, MG.GRID_ID

insert.foreach:

INTO MOM_GRID (
             COMPANY_CD
           , DIVISION_CD
           , MENU_ID
           , GRID_ID
           , GRID_NM
           , USE_YN
           , DESCRIPTION
           , CREATE_BY
           , CREATE_DATE
           , UPDATE_BY
           , UPDATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.menuId, jdbcType=VARCHAR}
           , #{item.gridId, jdbcType=VARCHAR}
           , #{item.gridNm, jdbcType=VARCHAR}
           , #{item.useYn, jdbcType=VARCHAR}
           , #{item.description, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

update.foreach:
UPDATE MOM_GRID
SET    GRID_NM = #{item.gridNm, jdbcType=VARCHAR}
     , USE_YN = #{item.useYn, jdbcType=VARCHAR}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    MENU_ID = #{item.menuId, jdbcType=VARCHAR}
AND    GRID_ID = #{item.gridId, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_GRID


  (       COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     AND  MENU_ID = #{item.menuId, jdbcType=VARCHAR}
     AND  GRID_ID = #{item.gridId, jdbcType=VARCHAR}
  )
