mapper.select:
SELECT MB.ITEM_ID , MI.ITEM_NM , MB.WORK_CENTER_CD , MWC.WORK_CENTER_NM , MB.ROUTING_ID , MR.ROUTING_NM , MB.PRIORITY , MB.BOR_ID , ROWIDTOCHAR(MB.ROWID) AS KEY_ID FROM MOM_BOR MB , MOM_ITEM MI , MOM_WORK_CENTER MWC , MOM_ROUTING MR WHERE MB.COMPANY_CD = MI.COMPANY_CD AND MB.DIVISION_CD = MI.DIVISION_CD AND MB.ITEM_ID = MI.ITEM_ID AND MB.COMPANY_CD = MWC.COMPANY_CD AND MB.DIVISION_CD = MWC.DIVISION_CD AND MB.WORK_CENTER_CD = MWC.WORK_CENTER_CD AND MB.COMPANY_CD = MR.COMPANY_CD AND MB.DIVISION_CD = MR.DIVISION_CD AND MB.ROUTING_ID = MR.ROUTING_ID AND MB.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MB.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND ( UPPER(MB.ITEM_ID) LIKE '%' || UPPER(#{itemId, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemId , jdbcType=VARCHAR}) || '%' )
AND MB.USE_YN = 'Y' ORDER BY MI.ITEM_NM, MB.PRIORITY
