// DOC READY
jQuery(document).ready(function() {
    // MODAL
    //debugger;
  function adjustModalMaxHeightAndPosition(e) {
    jQuery(AmCharts.Editor.SELECTORS.modal).each(function(){
      if(jQuery(this).is('.in') == false){
        jQuery(this).show();
      };
      var contentHeight = jQuery(window).height() - 60;
      var headerHeight  = jQuery(this).find('.modal-header').outerHeight() || 2;
      var footerHeight  = jQuery(this).find('.modal-footer').outerHeight() || 2;
      var worthToSqueeze  = contentHeight <= 250;

      // APPLY BODY HEIGHT ONCE
      if ( e.type == 'shown' ) {
        jQuery(this).find('.modal-body').css({
          minHeight: function() {
            return jQuery(this).height() - (headerHeight + footerHeight);
          }
        });
      }

      jQuery(this).find('.modal-content').css({
        maxHeight: function () {
          return worthToSqueeze?'':contentHeight;
        }
      });

      jQuery(this).find('.modal-body').css({
        maxHeight: function () {
          return worthToSqueeze?'':contentHeight - (headerHeight + footerHeight);
        }
      });

      jQuery(this).find('.modal-dialog')[worthToSqueeze?'removeClass':'addClass']('modal-dialog-center').css({
        marginTop: function () {
          return worthToSqueeze?'':-(jQuery(this).outerHeight() / 2);
        },
        marginLeft: function () {
          return worthToSqueeze?'':-(jQuery(this).outerWidth() / 2);
        }
      });
      if(jQuery(this).is('.in') == false){
        jQuery(this).hide();
      };
    });
  };
  jQuery(AmCharts.Editor.SELECTORS.modal).on('show.bs.modal shown.bs.modal loaded.bs.modal adjustposition.bs.modal',adjustModalMaxHeightAndPosition);
  jQuery(AmCharts.Editor.SELECTORS.modal).on('hide.bs.modal',function() {
      if (jQuery.router.currentId == '/new/') {
      if ( AmCharts.Editor.current.chart.chart_id ) {
        jQuery.router.go('/' + AmCharts.Editor.current.chart.chart_id + '/edit/');
      } else if ( AmCharts.Editor.current.tpl.loaded ) {
        jQuery.router.go('/new/edit/');
      } else {
        jQuery.router.go('/');
      }
    } else {
      ZeroClipboard.destroy();
    }
  });
  jQuery(window).on('resize',adjustModalMaxHeightAndPosition).trigger("resize");
});

