mapper.select:
SELECT * FROM ( SELECT ROWNUM AS KEY_ID, COUNT(*) OVER() AS TOTAL_COUNT, A.*
FROM (SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MP.PARTNER_CD , MP.PARTNER_NM , MP.PARTNER_NM_ENG , MP.PARTNER_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MP.COMPANY_CD,MP.DIVISION_CD,'MD0018', MP.PARTNER_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL ) AS PARTNER_TYPE_NM , MP.BUSINESS_NO , MP.BUSINESS_ITEM , MP.BUSINESS_TYPE , MP.TEL_NO , MP.FAX_NO , MP.EMAIL , MP.ADDRESS , MP.REPRESENTATIVE_NM , MP.PARTNER_USER_NM , MP.USE_YN , MP.DESCRIPTION , MP.UPDATE_BY , MP.UPDATE_DATE , ( SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MP.COMPANY_CD,MP.DIVISION_CD,MP.UPDATE_BY) FROM DUAL) AS UPDATE_BY_NM FROM MOM_COM_DIV_V MCD , MOM_PARTNER MP WHERE MCD.COMPANY_CD = MP.COMPANY_CD AND MCD.DIVISION_CD = MP.DIVISION_CD AND MCD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (UPPER(MP.PARTNER_CD) LIKE '%' || UPPER(#{partnerCd, jdbcType=VARCHAR}) || '%' )
AND (TRIM(UPPER(MP.PARTNER_NM)) LIKE '%' || TRIM(UPPER(#{partnerNm, jdbcType=VARCHAR})) || '%' )
AND MP.PARTNER_TYPE IN (${partnerType})
AND MP.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MP.PARTNER_NM ) A )
WHERE KEY_ID BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

