mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MWG.WORK_GROUP_CD , MWG.WORK_GROUP_NM , MWG.USE_YN , MWG.DESCRIPTION , MWG.CREATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MWG.COMPANY_CD, MWG.DIVISION_CD, MWG.CREATE_BY) FROM DUAL ) AS CREATE_BY_NM , TO_CHAR(MWG.CREATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , MWG.UPDATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MWG.COMPANY_CD, MWG.DIVISION_CD, MWG.UPDATE_BY) FROM DUAL ) AS UPDATE_BY_NM , TO_CHAR(MWG.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE FROM MOM_COM_DIV_V MCD , MOM_WORK_GROUP MWG WHERE MCD.COMPANY_CD = MWG.COMPANY_CD AND MCD.DIVISION_CD = MWG.DIVISION_CD AND MCD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (MWG.WORK_GROUP_CD LIKE '%' || TRIM(UPPER(#{workGroupCd, jdbcType=VARCHAR})) || '%' )
AND (MWG.WORK_GROUP_NM LIKE '%' || TRIM(UPPER(#{workGroupNm, jdbcType=VARCHAR})) || '%' )
AND MWG.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MWG.WORK_GROUP_NM

mapper.insert:
INSERT INTO MOM_WORK_GROUP (
                        COMPANY_CD
                     ,  DIVISION_CD
                     ,  WORK_GROUP_CD
                     ,  WORK_GROUP_NM
                     ,  USE_YN
                     ,  DESCRIPTION
                     ,  CREATE_BY
                     ,  CREATE_DATE
                     ,  UPDATE_BY
                     ,  UPDATE_DATE )
VALUES 
                     (   #{item.companyCd, jdbcType=VARCHAR}
                     ,   #{item.divisionCd, jdbcType=VARCHAR}
                     ,   #{item.workGroupCd, jdbcType=VARCHAR}
                     ,   #{item.workGroupNm, jdbcType=VARCHAR}
                     ,   #{item.useYn, jdbcType=VARCHAR}
                     ,   #{item.description, jdbcType=VARCHAR}
                     ,   #{item.userId, jdbcType=VARCHAR}
                     ,   MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                     ,   #{item.userId, jdbcType=VARCHAR}
                     ,   MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                     )

INSERT INTO MOM_WORK_GROUP (
                        COMPANY_CD
                     ,  DIVISION_CD
                     ,  WORK_GROUP_CD
                     ,  WORK_GROUP_NM
                     ,  USE_YN
                     ,  DESCRIPTION
                     ,  CREATE_BY
                     ,  CREATE_DATE
                     ,  UPDATE_BY
                     ,  UPDATE_DATE )
VALUES 
                     (   #{item.companyCd, jdbcType=VARCHAR}
                     ,   #{item.divisionCd, jdbcType=VARCHAR}
                     ,   #{item.workGroupCd, jdbcType=VARCHAR}
                     ,   #{item.workGroupNm, jdbcType=VARCHAR}
                     ,   #{item.useYn, jdbcType=VARCHAR}
                     ,   #{item.description, jdbcType=VARCHAR}
                     ,   #{item.userId, jdbcType=VARCHAR}
                     ,   MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                     ,   #{item.userId, jdbcType=VARCHAR}
                     ,   MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                     )

update.foreach:
UPDATE MOM_WORK_GROUP
SET    COMPANY_CD    = #{item.companyCd,     jdbcType=VARCHAR}
    ,  DIVISION_CD   = #{item.divisionCd,    jdbcType=VARCHAR}
    ,  WORK_GROUP_CD = #{item.workGroupCd,   jdbcType=VARCHAR}
    ,  WORK_GROUP_NM = #{item.workGroupNm,   jdbcType=VARCHAR}
    ,  USE_YN        = #{item.useYn,         jdbcType=VARCHAR}
    ,  DESCRIPTION   = #{item.description,   jdbcType=VARCHAR}
    ,  UPDATE_BY     = #{item.userId,        jdbcType=VARCHAR}
    ,  UPDATE_DATE   = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE 1=1
AND   COMPANY_CD     = TRIM(#{item.companyCd,   jdbcType=VARCHAR})
AND   DIVISION_CD    = TRIM(#{item.divisionCd,  jdbcType=VARCHAR}) 
AND   WORK_GROUP_CD  = TRIM(#{item.workGroupCd, jdbcType=VARCHAR})
