mapper.select:
SELECT MPD.PR_NO , MPD.PR_NO || '_' || MPD.PURCHASE_REQUEST_ID AS KEY_ID , MPD.PR_SEQ , MPM.PR_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0001', MPM.PR_TYPE, 'KR') FROM DUAL) AS PR_TYPE_NM , MPM.PR_TITLE , MPM.REQUEST_DEPT_CD , (SELECT MOM_COMMON_PKG.FN_GET_DEPARTMENT_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.REQUEST_DEPT_CD) FROM DUAL) AS REQUEST_DEPT_NM , MPM.REQUEST_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.REQUEST_USER_NO) FROM DUAL ) AS REQUEST_USER_NM , MPD.PO_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.PO_USER_NO) FROM DUAL ) AS PO_USER_NM , CASE WHEN MPD.PO_USER_NO IS NULL THEN MPD.PO_USER_NO ELSE MPD.PO_USER_NO||'('||(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.PO_USER_NO) FROM DUAL )||')' END AS PO_USER_NO_NM , MPM.CONFIRM_DATE , MPD.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, 'KR') FROM DUAL) AS ITEM_TYPE_NM , MI.PURCHASE_UNIT , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.PURCHASE_UNIT) FROM DUAL) AS PURCHASE_UNIT_NM , MPD.DUE_DATE , MPD.CONFIRM_QTY AS REQUEST_QTY , MPD.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'SM0017', MPD.CURRENCY_CD, 'KR') FROM DUAL) AS CURRENCY_NM , MPD.UNIT_PRICE , MPD.AMOUNT , MPD.LOCAL_AMOUNT , MI.IN_LOCATION_CD AS DEPARTURE_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MPD.COMPANY_CD,MPD.DIVISION_CD,MI.IN_LOCATION_CD) FROM DUAL) AS DEPARTURE_LOCATION_NM , MI.MAKER , MI.LOT_MANAGEMENT_YN , MI.PRICE_SYNC_YN , MI.PO_OVER_RECEIPT_RATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0007', MI.MAKER, 'KR') FROM DUAL) AS MAKER_NM , MI.VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPD.COMPANY_CD,MPD.DIVISION_CD,MI.VENDOR_CD) FROM DUAL) AS VENDOR_NM , MI.DEPARTURE_VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPD.COMPANY_CD,MPD.DIVISION_CD,MI.DEPARTURE_VENDOR_CD) FROM DUAL) AS DEPARTURE_VENDOR_NM , MI.PROCUREMENT_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0004', MI.PROCUREMENT_TYPE, 'KR') FROM DUAL) AS PROCUREMENT_TYPE_NM , MPM.REFERENCE_NO , MPM.CONFIRM_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_DEPARTMENT_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.CONFIRM_USER_NO) FROM DUAL) AS CONFIRM_USER_NM , MPD.DESCRIPTION , MPD.COMPANY_CD , MPD.DIVISION_CD , MPD.PURCHASE_REQUEST_ID , 'PR' AS CATEGORY FROM MOM_PR_DTL MPD , MOM_PR_MST MPM , MOM_ITEM MI WHERE MPD.COMPANY_CD = MPM.COMPANY_CD AND MPD.DIVISION_CD = MPM.DIVISION_CD AND MPD.PR_NO = MPM.PR_NO AND MPD.COMPANY_CD = MI.COMPANY_CD AND MPD.DIVISION_CD = MI.DIVISION_CD AND MPD.ITEM_ID = MI.ITEM_ID AND MPD.STATE = 'PRF' AND MPM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPM.CONFIRM_DATE BETWEEN TO_DATE(#{confirmDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{confirmDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MPM.PR_NO) LIKE '%' || UPPER(#{prNo, jdbcType=VARCHAR}) || '%')
AND MPM.REQUEST_USER_NO = #{requestUserNo, jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND MPM.REQUEST_DEPT_CD = #{requestDeptCd, jdbcType=VARCHAR}
AND MI.ITEM_TYPE = #{itemType, jdbcType=VARCHAR}
ORDER BY MPD.PR_NO , MPD.PR_SEQ

mapper.delete:
DELETE FROM MOM_PROC_RECEIVE_SAVE


 (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
    AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
    AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
 )

insert.foreach:

INTO MOM_PROC_RECEIVE_SAVE (
             COMPANY_CD
           , DIVISION_CD
           , PR_NO
           , PURCHASE_REQUEST_ID
           , ITEM_TYPE
           , PO_USER_NO
           , DESCRIPTION
           , VAL_MSG
           , MULTI_MSG
           , CREATE_BY
           , CREATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.prNo, jdbcType=VARCHAR}
           , #{item.purchaseRequestId, jdbcType=VARCHAR}
           , #{item.itemType, jdbcType=VARCHAR}
           , #{item.poUserNo, jdbcType=VARCHAR}
           , #{item.description, jdbcType=VARCHAR}
           , #{item.valMsg, jdbcType=VARCHAR}
           , #{item.multiMsg, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

mapper.select:

          { CALL MOM_MATERIAL_PKG.sp_pr_receive_save(     		#{p_err_code,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,   javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd,  jdbcType=VARCHAR, mode=IN}											  
					                                          , #{userId,      jdbcType=VARCHAR, mode=IN} )}

mapper.delete:
DELETE FROM MOM_PROC_PR_RECEIVE


 (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
    AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
    AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
 )

